import { createHash } from 'node:crypto'
import { existsSync } from 'node:fs'
import { mkdir, readFile, writeFile } from 'node:fs/promises'
import { join } from 'node:path'

import fg from 'fast-glob'

import { canonicalizeDefinitions, type MigrationOperation, type SchemaDefinition, type Snapshot } from '@chkit/core'

export interface MigrationJournalEntry {
  name: string
  appliedAt: string
  checksum: string
}

export interface MigrationJournal {
  version: 1
  applied: MigrationJournalEntry[]
}

export interface ChecksumMismatch {
  name: string
  expected: string
  actual: string
}

export function parseJSONOrThrow<T>(raw: string, filePath: string, kind: string): T {
  try {
    return JSON.parse(raw) as T
  } catch {
    throw new Error(`Invalid ${kind} JSON at ${filePath}. Fix or remove the file and retry.`)
  }
}

export async function readSnapshot(metaDir: string): Promise<Snapshot | null> {
  const file = join(metaDir, 'snapshot.json')
  if (!existsSync(file)) return null
  const raw = await readFile(file, 'utf8')
  const parsed = parseJSONOrThrow<Partial<Snapshot> & { definitions?: SchemaDefinition[] }>(
    raw,
    file,
    'snapshot'
  )
  return {
    version: 1,
    generatedAt: parsed.generatedAt ?? '',
    definitions: canonicalizeDefinitions(parsed.definitions ?? []),
  }
}

export function summarizePlan(operations: MigrationOperation[]): string[] {
  return operations.map((op) => `${op.type} [${op.risk}] ${op.key}`)
}

export async function readJournal(metaDir: string): Promise<MigrationJournal> {
  const file = join(metaDir, 'journal.json')
  if (!existsSync(file)) return { version: 1, applied: [] }
  const raw = await readFile(file, 'utf8')
  const parsed = parseJSONOrThrow<
    Partial<MigrationJournal> & { applied?: MigrationJournalEntry[] | string[] }
  >(raw, file, 'journal')

  const normalizedApplied: MigrationJournalEntry[] = []
  for (const item of parsed.applied ?? []) {
    if (typeof item === 'string') {
      normalizedApplied.push({
        name: item,
        appliedAt: '',
        checksum: '',
      })
      continue
    }
    if (!item || typeof item !== 'object') continue
    if (typeof item.name !== 'string') continue
    normalizedApplied.push({
      name: item.name,
      appliedAt: typeof item.appliedAt === 'string' ? item.appliedAt : '',
      checksum: typeof item.checksum === 'string' ? item.checksum : '',
    })
  }

  return {
    version: 1,
    applied: normalizedApplied,
  }
}

export async function writeJournal(metaDir: string, journal: MigrationJournal): Promise<void> {
  await mkdir(metaDir, { recursive: true })
  const file = join(metaDir, 'journal.json')
  await writeFile(file, `${JSON.stringify(journal, null, 2)}\n`, 'utf8')
}

export async function listMigrations(migrationsDir: string): Promise<string[]> {
  const files = await fg('*.sql', { cwd: migrationsDir, onlyFiles: true })
  return files.sort()
}

function checksum(value: string): string {
  return createHash('sha256').update(value, 'utf8').digest('hex')
}

export async function findChecksumMismatches(
  migrationsDir: string,
  journal: MigrationJournal
): Promise<ChecksumMismatch[]> {
  const mismatches: ChecksumMismatch[] = []
  for (const entry of journal.applied) {
    if (!entry.checksum) continue
    const fullPath = join(migrationsDir, entry.name)
    if (!existsSync(fullPath)) continue
    const sql = await readFile(fullPath, 'utf8')
    const actual = checksum(sql)
    if (actual !== entry.checksum) {
      mismatches.push({
        name: entry.name,
        expected: entry.checksum,
        actual,
      })
    }
  }
  return mismatches
}

export function checksumSQL(value: string): string {
  return checksum(value)
}

import { mkdir, readFile } from 'node:fs/promises'
import { join } from 'node:path'
import process from 'node:process'
import { createInterface } from 'node:readline/promises'

import { createClickHouseExecutor } from '@chkit/clickhouse'

import { defineFlags, typedFlags, type CommandDef, type CommandRunContext } from '../../plugins.js'
import { GLOBAL_FLAGS } from '../global-flags.js'
import { emitJson } from '../json-output.js'
import {
  checksumSQL,
  findChecksumMismatches,
  listMigrations,
  readJournal,
  readSnapshot,
  type MigrationJournalEntry,
  writeJournal,
} from '../migration-store.js'
import {
  collectDestructiveOperationMarkers,
  extractExecutableStatements,
  extractMigrationOperationSummaries,
  migrationContainsDangerOperation,
  type DestructiveOperationMarker,
} from '../safety-markers.js'
import { databaseKeyFromOperationKey, resolveTableScope, tableKeyFromOperationKey, tableKeysFromDefinitions } from '../table-scope.js'

const MIGRATE_FLAGS = defineFlags([
  { name: '--apply', type: 'boolean', description: 'Apply pending migrations on ClickHouse (no prompt)' },
  { name: '--execute', type: 'boolean', description: 'Alias for --apply' },
  { name: '--allow-destructive', type: 'boolean', description: 'Allow destructive migrations tagged with risk=danger' },
] as const)

export const migrateCommand: CommandDef = {
  name: 'migrate',
  description: 'Review or execute pending migrations',
  flags: MIGRATE_FLAGS,
  run: cmdMigrate,
}

function isBackgroundOrCI(): boolean {
  return process.env.CI === '1' || process.env.CI === 'true' || !process.stdin.isTTY || !process.stdout.isTTY
}

function printDestructiveOperationDetails(markers: DestructiveOperationMarker[]): void {
  console.log('Destructive operations detected:')
  for (const [index, marker] of markers.entries()) {
    console.log(`${index + 1}. ${marker.migration}`)
    console.log(`   operation: ${marker.type}`)
    console.log(`   key: ${marker.key}`)
    console.log(`   warning: ${marker.warningCode}`)
    console.log(`   reason: ${marker.reason}`)
    console.log(`   impact: ${marker.impact}`)
    console.log(`   recommendation: ${marker.recommendation}`)
  }
}

async function confirmDestructiveExecution(markers: DestructiveOperationMarker[]): Promise<boolean> {
  const rl = createInterface({ input: process.stdin, output: process.stdout })
  try {
    printDestructiveOperationDetails(markers)
    console.log('')
    console.log('Type "yes" to continue. Any other input cancels.')
    const response = await rl.question('Apply destructive operations? [no/yes]: ')
    return response.trim().toLowerCase() === 'yes'
  } finally {
    rl.close()
  }
}

async function filterPendingByScope(
  migrationsDir: string,
  pending: string[],
  selectedTables: ReadonlySet<string>
): Promise<string[]> {
  const selectedDatabases = new Set([...selectedTables].map((table) => table.split('.')[0] ?? ''))

  const inScope: string[] = []
  for (const file of pending) {
    const sql = await readFile(join(migrationsDir, file), 'utf8')
    const operations = extractMigrationOperationSummaries(sql)
    const matches = operations.some((operation) => {
      const tableKey = tableKeyFromOperationKey(operation.key)
      if (tableKey) return selectedTables.has(tableKey)

      const database = databaseKeyFromOperationKey(operation.key)
      if (database) return selectedDatabases.has(database)

      return false
    })

    if (matches) inScope.push(file)
  }

  return inScope
}

async function cmdMigrate(ctx: CommandRunContext): Promise<void> {
  const { flags, config, configPath, dirs, pluginRuntime } = ctx
  const f = typedFlags(flags, [...GLOBAL_FLAGS, ...MIGRATE_FLAGS] as const)
  const executeRequested = f['--apply'] === true || f['--execute'] === true
  const allowDestructive = f['--allow-destructive'] === true
  const tableSelector = f['--table']
  const jsonMode = f['--json'] === true

  const { migrationsDir, metaDir } = dirs
  const snapshot = await readSnapshot(metaDir)
  const tableScope = resolveTableScope(tableSelector, tableKeysFromDefinitions(snapshot?.definitions ?? []))

  await pluginRuntime.runOnConfigLoaded({
    command: 'migrate',
    config,
    configPath,
    tableScope,
    flags,
  })

  await mkdir(migrationsDir, { recursive: true })
  const files = await listMigrations(migrationsDir)
  const journal = await readJournal(metaDir)
  const appliedNames = new Set(journal.applied.map((entry) => entry.name))
  const pendingAll = files.filter((f) => !appliedNames.has(f))
  const checksumMismatches = await findChecksumMismatches(migrationsDir, journal)

  if (checksumMismatches.length > 0) {
    if (jsonMode) {
      emitJson('migrate', {
        mode: executeRequested ? 'execute' : 'plan',
        scope: tableScope,
        error: 'Checksum mismatch detected on applied migrations',
        checksumMismatches,
      })
      process.exitCode = 1
      return
    }
    throw new Error(
      `Checksum mismatch detected on applied migrations: ${checksumMismatches
        .map((item) => item.name)
        .join(', ')}`
    )
  }

  if (tableScope.enabled && tableScope.matchCount === 0) {
    if (jsonMode) {
      emitJson('migrate', {
        mode: executeRequested ? 'execute' : 'plan',
        scope: tableScope,
        pending: [],
        applied: [],
        warning: `No tables matched selector "${tableScope.selector ?? ''}".`,
      })
    } else {
      console.log(`No tables matched selector "${tableScope.selector ?? ''}". No migrations selected.`)
    }
    return
  }

  const pending = tableScope.enabled
    ? await filterPendingByScope(migrationsDir, pendingAll, new Set(tableScope.matchedTables))
    : pendingAll

  if (pending.length === 0) {
    if (jsonMode) {
      emitJson('migrate', {
        mode: executeRequested ? 'execute' : 'plan',
        scope: tableScope,
        pending: [],
        applied: [],
      })
    } else {
      console.log('No pending migrations.')
    }
    return
  }

  const planned = {
    mode: executeRequested ? 'execute' : 'plan',
    scope: tableScope,
    pending,
  }

  if (jsonMode && !executeRequested) {
    emitJson('migrate', planned)
    return
  }

  if (!jsonMode) {
    if (tableScope.enabled) {
      console.log(`Table scope: ${tableScope.selector ?? ''} (${tableScope.matchCount} matched)`)
      for (const table of tableScope.matchedTables) console.log(`- ${table}`)
    }
    console.log(`Pending migrations: ${pending.length}`)
    for (const file of pending) console.log(`- ${file}`)
  }

  let shouldExecute = executeRequested
  if (!shouldExecute) {
    if (isBackgroundOrCI() || jsonMode) {
      if (!jsonMode) {
        console.log('\nPlan only. Re-run with --apply to apply and journal these migrations.')
      }
      return
    }

    const rl = createInterface({ input: process.stdin, output: process.stdout })
    try {
      console.log('')
      console.log('Type "yes" to continue. Any other input cancels.')
      const response = await rl.question('Apply pending migrations now? [no/yes]: ')
      shouldExecute = response.trim().toLowerCase() === 'yes'
    } finally {
      rl.close()
    }

    if (!shouldExecute) {
      console.log('Migration apply cancelled by user.')
      return
    }
  }

  const destructiveMigrations: string[] = []
  const destructiveOperations: DestructiveOperationMarker[] = []
  for (const file of pending) {
    const sql = await readFile(join(migrationsDir, file), 'utf8')
    if (migrationContainsDangerOperation(sql)) {
      destructiveMigrations.push(file)
      destructiveOperations.push(...collectDestructiveOperationMarkers(file, sql))
    }
  }

  let destructiveAllowed = allowDestructive || config.safety?.allowDestructive === true
  if (destructiveMigrations.length > 0 && !destructiveAllowed) {
    const error =
      'Blocked destructive migration execution. Re-run with --allow-destructive or set safety.allowDestructive=true after review.'
    if (jsonMode) {
      emitJson('migrate', {
        mode: 'execute',
        scope: tableScope,
        error,
        destructiveMigrations,
        destructiveOperations,
      })
      process.exitCode = 3
      return
    }

    if (isBackgroundOrCI()) {
      printDestructiveOperationDetails(destructiveOperations)
      throw new Error(
        `${error}\nDestructive migrations: ${destructiveMigrations.join(', ')}\n` +
          'Non-interactive run detected. Pass --allow-destructive to proceed.'
      )
    }

    const confirmed = await confirmDestructiveExecution(destructiveOperations)
    if (!confirmed) {
      throw new Error(
        `Destructive migration cancelled by user.\nDestructive migrations: ${destructiveMigrations.join(', ')}`
      )
    }
    destructiveAllowed = true
  }

  if (destructiveMigrations.length > 0 && !destructiveAllowed) {
    throw new Error('Blocked destructive migration execution.')
  }

  if (!config.clickhouse) {
    throw new Error('clickhouse config is required for --apply')
  }

  const db = createClickHouseExecutor(config.clickhouse)
  const appliedNow: MigrationJournalEntry[] = []

  for (const file of pending) {
    const fullPath = join(migrationsDir, file)
    const sql = await readFile(fullPath, 'utf8')
    const parsedStatements = extractExecutableStatements(sql)
    const statements = await pluginRuntime.runOnBeforeApply({
      command: 'migrate',
      config,
      tableScope,
      flags,
      migration: file,
      sql,
      statements: parsedStatements,
    })
    for (const statement of statements) {
      await db.execute(statement)
    }

    const entry: MigrationJournalEntry = {
      name: file,
      appliedAt: new Date().toISOString(),
      checksum: checksumSQL(sql),
    }
    journal.applied.push(entry)
    appliedNow.push(entry)
    await writeJournal(metaDir, journal)
    await pluginRuntime.runOnAfterApply({
      command: 'migrate',
      config,
      tableScope,
      flags,
      migration: file,
      statements,
      appliedAt: entry.appliedAt,
    })

    if (!jsonMode) console.log(`Applied: ${file}`)
  }

  if (jsonMode) {
    emitJson('migrate', {
      mode: 'execute',
      scope: tableScope,
      applied: appliedNow,
      journalFile: join(metaDir, 'journal.json'),
    })
    return
  }

  console.log(`\nJournal updated: ${join(metaDir, 'journal.json')}`)
}

import { mkdir } from 'node:fs/promises'

import type { CommandDef, CommandRunContext } from '../../plugins.js'
import { emitJson } from '../json-output.js'
import { findChecksumMismatches, listMigrations, readJournal } from '../migration-store.js'

export const statusCommand: CommandDef = {
  name: 'status',
  description: 'Show migration status and checksum mismatch information',
  flags: [],
  run: cmdStatus,
}

async function cmdStatus(ctx: CommandRunContext): Promise<void> {
  const { flags, dirs } = ctx
  const jsonMode = flags['--json'] === true
  const { migrationsDir, metaDir } = dirs

  await mkdir(migrationsDir, { recursive: true })
  const files = await listMigrations(migrationsDir)
  const journal = await readJournal(metaDir)
  const appliedNames = new Set(journal.applied.map((entry) => entry.name))
  const pending = files.filter((f) => !appliedNames.has(f))
  const checksumMismatches = await findChecksumMismatches(migrationsDir, journal)

  const payload = {
    migrationsDir,
    total: files.length,
    applied: journal.applied.length,
    pending: pending.length,
    pendingMigrations: pending,
    checksumMismatchCount: checksumMismatches.length,
    checksumMismatches,
  }

  if (jsonMode) {
    emitJson('status', payload)
    return
  }

  console.log(`Migrations directory: ${migrationsDir}`)
  console.log(`Total migrations:     ${files.length}`)
  console.log(`Applied:              ${journal.applied.length}`)
  console.log(`Pending:              ${pending.length}`)

  if (pending.length > 0) {
    console.log('\nPending migrations:')
    for (const item of pending) console.log(`- ${item}`)
  }
  if (checksumMismatches.length > 0) {
    console.log('\nChecksum mismatches on applied migrations:')
    for (const item of checksumMismatches) console.log(`- ${item.name}`)
  }
}

import { mkdir } from 'node:fs/promises'

import { summarizeDriftReasons } from '../../drift.js'
import type { CommandDef, CommandRunContext } from '../../plugins.js'
import { emitJson } from '../json-output.js'
import { findChecksumMismatches, listMigrations, readJournal, readSnapshot } from '../migration-store.js'
import { buildDriftPayload } from './drift.js'

export const checkCommand: CommandDef = {
  name: 'check',
  description: 'Run policy checks for CI and release gates',
  flags: [
    { name: '--strict', type: 'boolean', description: 'Enable all policy checks' },
  ],
  run: cmdCheck,
}

async function cmdCheck(ctx: CommandRunContext): Promise<void> {
  const { flags, config, configPath, dirs, pluginRuntime } = ctx
  const strict = flags['--strict'] === true
  const jsonMode = flags['--json'] === true
  const { migrationsDir, metaDir } = dirs
  await mkdir(migrationsDir, { recursive: true })

  const files = await listMigrations(migrationsDir)
  const journal = await readJournal(metaDir)
  const appliedNames = new Set(journal.applied.map((entry) => entry.name))
  const pending = files.filter((f) => !appliedNames.has(f))
  const checksumMismatches = await findChecksumMismatches(migrationsDir, journal)
  const snapshot = await readSnapshot(metaDir)
  const drift = snapshot && config.clickhouse ? await buildDriftPayload(config, metaDir, snapshot) : null

  const policy = {
    failOnPending: strict ? true : config.check?.failOnPending ?? true,
    failOnChecksumMismatch: strict ? true : config.check?.failOnChecksumMismatch ?? true,
    failOnDrift: strict ? true : config.check?.failOnDrift ?? true,
  }

  await pluginRuntime.runOnConfigLoaded({
    command: 'check',
    config,
    configPath,
    flags,
  })
  const pluginResults = await pluginRuntime.runOnCheck({
    command: 'check',
    config,
    configPath,
    jsonMode,
    flags,
  })

  const failedChecks: string[] = []
  if (policy.failOnPending && pending.length > 0) failedChecks.push('pending_migrations')
  if (policy.failOnChecksumMismatch && checksumMismatches.length > 0) failedChecks.push('checksum_mismatch')
  if (policy.failOnDrift && drift?.drifted) failedChecks.push('schema_drift')
  for (const result of pluginResults) {
    const hasErrorFinding = result.findings.some((finding) => finding.severity === 'error')
    if (result.evaluated && !result.ok && hasErrorFinding) {
      failedChecks.push(`plugin:${result.plugin}`)
    }
  }
  const ok = failedChecks.length === 0
  const driftReasonSummary = drift
    ? summarizeDriftReasons({
        objectDrift: drift.objectDrift,
        tableDrift: drift.tableDrift,
      })
    : { counts: {}, total: 0, object: 0, table: 0 }

  const payload = {
    strict,
    policy,
    ok,
    failedChecks,
    pendingCount: pending.length,
    checksumMismatchCount: checksumMismatches.length,
    drifted: drift?.drifted ?? false,
    driftEvaluated: drift !== null,
    driftReasonCounts: driftReasonSummary.counts,
    driftReasonTotals: {
      total: driftReasonSummary.total,
      object: driftReasonSummary.object,
      table: driftReasonSummary.table,
    },
    plugins: Object.fromEntries(
      pluginResults.map((result) => [
        result.plugin,
        {
          evaluated: result.evaluated,
          ok: result.ok,
          findingCodes: result.findings.map((finding) => finding.code),
          ...(result.metadata ?? {}),
        },
      ])
    ),
  }

  if (jsonMode) {
    emitJson('check', payload)
    if (!ok) process.exitCode = 1
    return
  }

  console.log(`Check status: ${ok ? 'ok' : 'failed'}`)
  console.log(
    `Policy: pending=${policy.failOnPending ? 'on' : 'off'}, checksum=${policy.failOnChecksumMismatch ? 'on' : 'off'}, drift=${policy.failOnDrift ? 'on' : 'off'}`
  )
  console.log(`Pending migrations: ${pending.length}`)
  console.log(`Checksum mismatches: ${checksumMismatches.length}`)
  if (drift === null) {
    console.log('Schema drift: not evaluated (missing snapshot or clickhouse config)')
  } else {
    console.log(`Schema drift: ${drift.drifted ? 'yes' : 'no'}`)
    const summary = summarizeDriftReasons({
      objectDrift: drift.objectDrift,
      tableDrift: drift.tableDrift,
    })
    console.log(
      `Drift reasons: total=${summary.total}, object=${summary.object}, table=${summary.table}`
    )
  }
  if (pluginResults.length > 0) {
    console.log('Plugin checks:')
    for (const result of pluginResults) {
      const findingCodes = result.findings.map((finding) => finding.code)
      console.log(
        `- ${result.plugin}: ${result.ok ? 'ok' : 'failed'}${findingCodes.length > 0 ? ` (${findingCodes.join(', ')})` : ''}`
      )
    }
    await pluginRuntime.runOnCheckReport(pluginResults, (line) => {
      console.log(line)
    })
  }
  if (!ok) {
    console.log(`Failed checks: ${failedChecks.join(', ')}`)
    process.exitCode = 1
  }
}

import { createClient } from '@clickhouse/client'
import {
  normalizeSQLFragment,
  type ChxConfig,
  type ColumnDefinition,
  type ProjectionDefinition,
  type SkipIndexDefinition,
} from '@chkit/core'
import {
  parseEngineFromCreateTableQuery,
  parseOrderByFromCreateTableQuery,
  parsePartitionByFromCreateTableQuery,
  parsePrimaryKeyFromCreateTableQuery,
  parseProjectionsFromCreateTableQuery,
  parseSettingsFromCreateTableQuery,
  parseTTLFromCreateTableQuery,
  parseUniqueKeyFromCreateTableQuery,
} from './create-table-parser.js'

export interface ClickHouseExecutor {
  execute(sql: string): Promise<void>
  query<T>(sql: string): Promise<T[]>
  insert<T extends Record<string, unknown>>(params: { table: string; values: T[] }): Promise<void>
  listSchemaObjects(): Promise<SchemaObjectRef[]>
  listTableDetails(databases: string[]): Promise<IntrospectedTable[]>
  close(): Promise<void>
}

export interface SchemaObjectRef {
  kind: 'table' | 'view' | 'materialized_view'
  database: string
  name: string
}

interface SystemTableRow {
  database: string
  name: string
  engine: string
  create_table_query?: string
}

interface SystemColumnRow {
  database: string
  table: string
  name: string
  type: string
  default_kind?: string
  default_expression?: string
  comment?: string
  position: number
}

interface SystemSkippingIndexRow {
  database: string
  table: string
  name: string
  expr: string
  type: string
  granularity: number
}

export interface IntrospectedTable {
  database: string
  name: string
  engine?: string
  primaryKey?: string
  orderBy?: string
  uniqueKey?: string
  partitionBy?: string
  columns: ColumnDefinition[]
  settings: Record<string, string>
  indexes: SkipIndexDefinition[]
  projections: ProjectionDefinition[]
  ttl?: string
}

export {
  parseEngineFromCreateTableQuery,
  parseOrderByFromCreateTableQuery,
  parsePartitionByFromCreateTableQuery,
  parsePrimaryKeyFromCreateTableQuery,
  parseProjectionsFromCreateTableQuery,
  parseSettingsFromCreateTableQuery,
  parseTTLFromCreateTableQuery,
  parseUniqueKeyFromCreateTableQuery,
} from './create-table-parser.js'

export function inferSchemaKindFromEngine(engine: string): SchemaObjectRef['kind'] | null {
  if (engine === 'View') return 'view'
  if (engine === 'MaterializedView') return 'materialized_view'
  if (!engine || engine === 'Dictionary') return null
  return 'table'
}


function normalizeColumnFromSystemRow(row: SystemColumnRow): ColumnDefinition {
  const nullableMatch = row.type.match(/^Nullable\((.+)\)$/)
  const type = nullableMatch?.[1] ? nullableMatch[1] : row.type
  const nullable = Boolean(nullableMatch?.[1])
  let defaultValue: ColumnDefinition['default'] | undefined
  if (row.default_expression && row.default_kind === 'DEFAULT') {
    defaultValue = normalizeSQLFragment(row.default_expression)
  }
  return {
    name: row.name,
    type,
    nullable: nullable || undefined,
    default: defaultValue,
    comment: row.comment?.trim() || undefined,
  }
}

function normalizeIndexType(value: string): SkipIndexDefinition['type'] {
  if (value === 'minmax') return 'minmax'
  if (value === 'set') return 'set'
  if (value === 'bloom_filter') return 'bloom_filter'
  if (value === 'tokenbf_v1') return 'tokenbf_v1'
  if (value === 'ngrambf_v1') return 'ngrambf_v1'
  return 'set'
}

function normalizeIndexFromSystemRow(row: SystemSkippingIndexRow): SkipIndexDefinition {
  return {
    name: row.name,
    expression: normalizeSQLFragment(row.expr),
    type: normalizeIndexType(row.type),
    granularity: row.granularity,
  }
}

export function createClickHouseExecutor(config: NonNullable<ChxConfig['clickhouse']>): ClickHouseExecutor {
  const client = createClient({
    url: config.url,
    username: config.username,
    password: config.password,
    database: config.database,
    clickhouse_settings: {
      wait_end_of_query: 1,
    },
  })

  return {
    async execute(sql: string): Promise<void> {
      await client.command({ query: sql })
    },
    async query<T>(sql: string): Promise<T[]> {
      const result = await client.query({ query: sql, format: 'JSONEachRow' })
      return result.json<T>()
    },
    async insert<T extends Record<string, unknown>>(params: { table: string; values: T[] }): Promise<void> {
      await client.insert({
        table: params.table,
        values: params.values,
        format: 'JSONEachRow',
      })
    },
    async close(): Promise<void> {
      await client.close()
    },
    async listSchemaObjects(): Promise<SchemaObjectRef[]> {
      const rows = await this.query<SystemTableRow>(
        `SELECT database, name, engine
FROM system.tables
WHERE is_temporary = 0
  AND database NOT IN ('system', 'information_schema', 'INFORMATION_SCHEMA')`
      )

      const out: SchemaObjectRef[] = []
      for (const row of rows) {
        const kind = inferSchemaKindFromEngine(row.engine)
        if (!kind) continue
        out.push({
          kind,
          database: row.database,
          name: row.name,
        })
      }
      return out
    },
    async listTableDetails(databases: string[]): Promise<IntrospectedTable[]> {
      if (databases.length === 0) return []

      const quotedDatabases = databases.map((dbName) => `'${dbName.replace(/'/g, "''")}'`).join(', ')
      const tables = await this.query<SystemTableRow>(
        `SELECT database, name, engine, create_table_query
FROM system.tables
WHERE is_temporary = 0
  AND database IN (${quotedDatabases})`
      )
      const tableRows = tables.filter((row) => inferSchemaKindFromEngine(row.engine) === 'table')
      if (tableRows.length === 0) return []

      const columns = await this.query<SystemColumnRow>(
        `SELECT database, table, name, type, default_kind, default_expression, comment, position
FROM system.columns
WHERE database IN (${quotedDatabases})`
      )
      const indexes = await this.query<SystemSkippingIndexRow>(
        `SELECT database, table, name, expr, type, granularity
FROM system.data_skipping_indices
WHERE database IN (${quotedDatabases})`
      )

      const columnsByTable = new Map<string, SystemColumnRow[]>()
      for (const row of columns) {
        const key = `${row.database}.${row.table}`
        const rows = columnsByTable.get(key)
        if (rows) {
          rows.push(row)
        } else {
          columnsByTable.set(key, [row])
        }
      }

      const indexesByTable = new Map<string, SystemSkippingIndexRow[]>()
      for (const row of indexes) {
        const key = `${row.database}.${row.table}`
        const rows = indexesByTable.get(key)
        if (rows) {
          rows.push(row)
        } else {
          indexesByTable.set(key, [row])
        }
      }

      return tableRows
        .map((row) => {
          const key = `${row.database}.${row.name}`
          const columnRows = (columnsByTable.get(key) ?? []).sort((a, b) => a.position - b.position)
          const indexRows = indexesByTable.get(key) ?? []
          return {
            database: row.database,
            name: row.name,
            engine: parseEngineFromCreateTableQuery(row.create_table_query),
            primaryKey: parsePrimaryKeyFromCreateTableQuery(row.create_table_query),
            orderBy: parseOrderByFromCreateTableQuery(row.create_table_query),
            uniqueKey: parseUniqueKeyFromCreateTableQuery(row.create_table_query),
            partitionBy: parsePartitionByFromCreateTableQuery(row.create_table_query),
            columns: columnRows.map(normalizeColumnFromSystemRow),
            settings: parseSettingsFromCreateTableQuery(row.create_table_query),
            indexes: indexRows.map(normalizeIndexFromSystemRow),
            projections: parseProjectionsFromCreateTableQuery(row.create_table_query),
            ttl: parseTTLFromCreateTableQuery(row.create_table_query),
          }
        })
        .sort((a, b) => {
          const dbOrder = a.database.localeCompare(b.database)
          if (dbOrder !== 0) return dbOrder
          return a.name.localeCompare(b.name)
        })
    },
  }
}

import { describe, expect, test } from 'bun:test'
import { mkdtemp, readFile, rm, writeFile } from 'node:fs/promises'
