const WORKSPACE_ROOT = resolve(import.meta.dir, '../../..')
const CLI_ENTRY = join(WORKSPACE_ROOT, 'packages/cli/src/bin/chkit.ts')
const CORE_ENTRY = join(WORKSPACE_ROOT, 'packages/core/src/index.ts')

function getRequiredEnv(): {
  clickhouseUrl: string
  clickhouseUser: string
  clickhousePassword: string
} {
  const clickhouseHost = process.env.CLICKHOUSE_HOST?.trim()
  const clickhouseUrl =
    process.env.CLICKHOUSE_URL?.trim() || (clickhouseHost ? `https://${clickhouseHost}` : '')
  const clickhouseUser = process.env.CLICKHOUSE_USER?.trim() || 'default'
  const clickhousePassword = process.env.CLICKHOUSE_PASSWORD?.trim() || ''

  if (!clickhouseUrl) {
    throw new Error('Missing CLICKHOUSE_URL or CLICKHOUSE_HOST')
  }

  if (!clickhousePassword) {
    throw new Error('Missing CLICKHOUSE_PASSWORD')
  }

  return { clickhouseUrl, clickhouseUser, clickhousePassword }
}

function runCli(cwd: string, args: string[]): { exitCode: number; stdout: string; stderr: string } {
  const result = Bun.spawnSync({
    cmd: ['bun', CLI_ENTRY, ...args],
    cwd,
    stdout: 'pipe',
    stderr: 'pipe',
    env: process.env,
  })

  return {
    exitCode: result.exitCode,
    stdout: new TextDecoder().decode(result.stdout),
    stderr: new TextDecoder().decode(result.stderr),
  }
}

async function dropDatabase(url: string, username: string, password: string, database: string): Promise<void> {
  const auth = Buffer.from(`${username}:${password}`).toString('base64')
  await fetch(url, {
    method: 'POST',
    signal: AbortSignal.timeout(10_000),
    headers: {
      Authorization: `Basic ${auth}`,
      'Content-Type': 'text/plain',
    },
    body: `DROP DATABASE IF EXISTS ${database}`,
  })
}

function renderBaseSchema(database: string): string {
  return `import { schema, table } from '${CORE_ENTRY}'\n\nconst users = table({\n  database: '${database}',\n  name: 'users',\n  columns: [\n    { name: 'id', type: 'UInt64' },\n    { name: 'email', type: 'String' },\n  ],\n  engine: 'MergeTree()',\n  primaryKey: ['id'],\n  orderBy: ['id'],\n})\n\nexport default schema(users)\n`
}

function renderEvolvedSchema(database: string): string {
  return `import { schema, table, view } from '${CORE_ENTRY}'\n\nconst users = table({\n  database: '${database}',\n  name: 'users',\n  columns: [\n    { name: 'id', type: 'UInt64' },\n    { name: 'email', type: 'String' },\n    { name: 'source', type: 'String', default: 'web' },\n  ],\n  engine: 'MergeTree()',\n  primaryKey: ['id'],\n  orderBy: ['id'],\n})\n\nconst usersView = view({\n  database: '${database}',\n  name: 'users_view',\n  as: 'SELECT id, email, source FROM ${database}.users',\n})\n\nexport default schema(users, usersView)\n`
}

interface E2EFixture {
  dir: string
  configPath: string
  migrationsDir: string
  schemaPath: string
}

async function createFixture(database: string): Promise<E2EFixture> {
  const dir = await mkdtemp(join(tmpdir(), 'chkit-cli-e2e-'))
  const schemaPath = join(dir, 'schema.ts')
  const configPath = join(dir, 'clickhouse.config.ts')
  const outDir = join(dir, 'chkit')
  const migrationsDir = join(outDir, 'migrations')
  const metaDir = join(outDir, 'meta')

  const { clickhouseUrl, clickhouseUser, clickhousePassword } = getRequiredEnv()

  await writeFile(schemaPath, renderBaseSchema(database), 'utf8')

  await writeFile(
    configPath,
    `export default {\n  schema: '${schemaPath}',\n  outDir: '${outDir}',\n  migrationsDir: '${migrationsDir}',\n  metaDir: '${metaDir}',\n  clickhouse: {\n    url: '${clickhouseUrl}',\n    username: '${clickhouseUser}',\n    password: '${clickhousePassword}',\n    database: 'default',\n  },\n}\n`,
    'utf8'
  )

  const auth = Buffer.from(`${clickhouseUser}:${clickhousePassword}`).toString('base64')
  await fetch(clickhouseUrl, {
    method: 'POST',
    signal: AbortSignal.timeout(10_000),
    headers: {
      Authorization: `Basic ${auth}`,
      'Content-Type': 'text/plain',
    },
    body: `CREATE DATABASE IF NOT EXISTS ${database}`,
  })

  return { dir, configPath, migrationsDir, schemaPath }
}

describe('@chkit/cli doppler env e2e', () => {
  const liveEnv = getRequiredEnv()

  test(
    'runs init + generate + migrate + status against live ClickHouse',
    async () => {
    const dbSuffix = `${Date.now()}_${Math.floor(Math.random() * 100000)}`
    const database = `chkit_e2e_${dbSuffix}`
    const fixture = await createFixture(database)
    const { clickhouseUrl, clickhouseUser, clickhousePassword } = liveEnv

    try {
      const initResult = runCli(fixture.dir, ['init'])
      expect(initResult.exitCode).toBe(0)

      const generateResult = runCli(fixture.dir, ['generate', '--config', fixture.configPath, '--json'])
      expect(generateResult.exitCode).toBe(0)
      const generatePayload = JSON.parse(generateResult.stdout) as { migrationFile: string | null }
      expect(generatePayload.migrationFile).toBeTruthy()
      if (!generatePayload.migrationFile) {
        throw new Error('expected generated migration file')
      }

      const planResult = runCli(fixture.dir, ['migrate', '--config', fixture.configPath, '--json'])
      expect(planResult.exitCode).toBe(0)
      const planPayload = JSON.parse(planResult.stdout) as { pending: string[] }
      expect(planPayload.pending.length).toBe(1)

      const executeResult = runCli(fixture.dir, ['migrate', '--config', fixture.configPath, '--execute', '--json'])
      if (executeResult.exitCode !== 0) {
        throw new Error(
          `migrate --execute failed (exit=${executeResult.exitCode})\nstdout:\n${executeResult.stdout}\nstderr:\n${executeResult.stderr}`
        )
      }
      expect(executeResult.exitCode).toBe(0)
      const executePayload = JSON.parse(executeResult.stdout) as {
        mode: string
        applied: Array<{ name: string }>
      }
      expect(executePayload.mode).toBe('execute')
      expect(executePayload.applied.length).toBe(1)

      const statusResult = runCli(fixture.dir, ['status', '--config', fixture.configPath, '--json'])
      expect(statusResult.exitCode).toBe(0)
      const statusPayload = JSON.parse(statusResult.stdout) as {
        total: number
        applied: number
        pending: number
        checksumMismatchCount: number
      }
      expect(statusPayload.total).toBe(1)
      expect(statusPayload.applied).toBe(1)
      expect(statusPayload.pending).toBe(0)
      expect(statusPayload.checksumMismatchCount).toBe(0)

      const generatedSqlPath = generatePayload.migrationFile.startsWith('/')
        ? generatePayload.migrationFile
        : join(fixture.migrationsDir, generatePayload.migrationFile)
      const generatedSql = await readFile(generatedSqlPath, 'utf8')
      expect(generatedSql.length).toBeGreaterThan(0)
    } finally {
      try {
        await dropDatabase(clickhouseUrl, clickhouseUser, clickhousePassword, database)
      } catch {
        // Best-effort cleanup for shared e2e environment.
      }
      await rm(fixture.dir, { recursive: true, force: true })
    }
    },
    240_000
  )

  test(
    'runs additive second migration cycle in a separate project flow',
    async () => {
      const dbSuffix = `${Date.now()}_${Math.floor(Math.random() * 100000)}`
      const database = `chkit_e2e_additive_${dbSuffix}`
      const fixture = await createFixture(database)
      const { clickhouseUrl, clickhouseUser, clickhousePassword } = liveEnv

      try {
        const firstGenerate = runCli(fixture.dir, ['generate', '--config', fixture.configPath, '--json'])
        expect(firstGenerate.exitCode).toBe(0)

        const firstExecute = runCli(fixture.dir, ['migrate', '--config', fixture.configPath, '--execute', '--json'])
        if (firstExecute.exitCode !== 0) {
          throw new Error(
            `first migrate --execute failed (exit=${firstExecute.exitCode})\nstdout:\n${firstExecute.stdout}\nstderr:\n${firstExecute.stderr}`
          )
        }
        expect(firstExecute.exitCode).toBe(0)

        await writeFile(fixture.schemaPath, renderEvolvedSchema(database), 'utf8')

        const secondGenerate = runCli(fixture.dir, ['generate', '--config', fixture.configPath, '--json'])
        expect(secondGenerate.exitCode).toBe(0)
        const secondGeneratePayload = JSON.parse(secondGenerate.stdout) as {
          operationCount: number
          migrationFile: string | null
        }
        expect(secondGeneratePayload.operationCount).toBeGreaterThan(0)
        expect(secondGeneratePayload.migrationFile).toBeTruthy()

        const secondPlan = runCli(fixture.dir, ['migrate', '--config', fixture.configPath, '--json'])
        if (secondPlan.exitCode !== 0) {
          throw new Error(
            `second migrate --json plan failed (exit=${secondPlan.exitCode})\nstdout:\n${secondPlan.stdout}\nstderr:\n${secondPlan.stderr}`
          )
        }
        expect(secondPlan.exitCode).toBe(0)
        const secondPlanPayload = JSON.parse(secondPlan.stdout) as { pending: string[] }
        expect(secondPlanPayload.pending.length).toBe(1)

        const secondExecute = runCli(fixture.dir, ['migrate', '--config', fixture.configPath, '--execute', '--json'])
        if (secondExecute.exitCode !== 0) {
          throw new Error(
            `second migrate --execute failed (exit=${secondExecute.exitCode})\nstdout:\n${secondExecute.stdout}\nstderr:\n${secondExecute.stderr}`
          )
        }
        expect(secondExecute.exitCode).toBe(0)

        const statusResult = runCli(fixture.dir, ['status', '--config', fixture.configPath, '--json'])
        expect(statusResult.exitCode).toBe(0)
        const statusPayload = JSON.parse(statusResult.stdout) as {
          total: number
          applied: number
          pending: number
        }
        expect(statusPayload.total).toBe(2)
        expect(statusPayload.applied).toBe(2)
        expect(statusPayload.pending).toBe(0)
      } finally {
        try {
          await dropDatabase(clickhouseUrl, clickhouseUser, clickhousePassword, database)
        } catch {
          // Best-effort cleanup for shared e2e environment.
        }
        await rm(fixture.dir, { recursive: true, force: true })
      }
    },
    240_000
  )

  test(
    'runs non-danger additive migrate path and ends with successful check',
    async () => {
      const dbSuffix = `${Date.now()}_${Math.floor(Math.random() * 100000)}`
      const database = `chkit_e2e_check_${dbSuffix}`
      const fixture = await createFixture(database)
      const { clickhouseUrl, clickhouseUser, clickhousePassword } = liveEnv

      try {
        const firstGenerate = runCli(fixture.dir, ['generate', '--config', fixture.configPath, '--json'])
        expect(firstGenerate.exitCode).toBe(0)
        const firstExecute = runCli(fixture.dir, ['migrate', '--config', fixture.configPath, '--execute', '--json'])
        if (firstExecute.exitCode !== 0) {
          throw new Error(
            `first migrate --execute failed (exit=${firstExecute.exitCode})\nstdout:\n${firstExecute.stdout}\nstderr:\n${firstExecute.stderr}`
          )
        }

        await writeFile(fixture.schemaPath, renderEvolvedSchema(database), 'utf8')

        const secondPlan = runCli(fixture.dir, ['generate', '--config', fixture.configPath, '--dryrun', '--json'])
        expect(secondPlan.exitCode).toBe(0)
        const secondPlanPayload = JSON.parse(secondPlan.stdout) as {
          operations: Array<{ type: string }>
        }
        expect(secondPlanPayload.operations.some((op) => op.type === 'drop_table')).toBe(false)

        const secondGenerate = runCli(fixture.dir, ['generate', '--config', fixture.configPath, '--json'])
        expect(secondGenerate.exitCode).toBe(0)
        const secondGeneratePayload = JSON.parse(secondGenerate.stdout) as { operationCount: number }
        expect(secondGeneratePayload.operationCount).toBeGreaterThan(0)

        const secondExecute = runCli(fixture.dir, ['migrate', '--config', fixture.configPath, '--execute', '--json'])
        if (secondExecute.exitCode !== 0) {
          throw new Error(
            `second migrate --execute failed (exit=${secondExecute.exitCode})\nstdout:\n${secondExecute.stdout}\nstderr:\n${secondExecute.stderr}`
          )
        }

        const check = runCli(fixture.dir, ['check', '--config', fixture.configPath, '--json'])
        expect(check.exitCode).toBe(0)
        const checkPayload = JSON.parse(check.stdout) as {
          ok: boolean
          failedChecks: string[]
          pendingCount: number
          checksumMismatchCount: number
          driftEvaluated: boolean
          drifted: boolean
        }
        expect(checkPayload.ok).toBe(true)
        expect(checkPayload.failedChecks).toEqual([])
        expect(checkPayload.pendingCount).toBe(0)
        expect(checkPayload.checksumMismatchCount).toBe(0)
        expect(checkPayload.driftEvaluated).toBe(true)
        expect(checkPayload.drifted).toBe(false)
      } finally {
        try {
          await dropDatabase(clickhouseUrl, clickhouseUser, clickhousePassword, database)
        } catch {
          // Best-effort cleanup for shared e2e environment.
        }
        await rm(fixture.dir, { recursive: true, force: true })
      }
    },
    240_000
  )

})

import { describe, expect, test } from 'bun:test'
import { mkdtemp, rm, writeFile } from 'node:fs/promises'
import { join, resolve } from 'node:path'
import { tmpdir } from 'node:os'

const WORKSPACE_ROOT = resolve(import.meta.dir, '../../..')
const CLI_ENTRY = join(WORKSPACE_ROOT, 'packages/cli/src/bin/chkit.ts')
const CORE_ENTRY = join(WORKSPACE_ROOT, 'packages/core/src/index.ts')

function getRequiredEnv(): {
  clickhouseUrl: string
  clickhouseUser: string
  clickhousePassword: string
} {
  const clickhouseHost = process.env.CLICKHOUSE_HOST?.trim()
  const clickhouseUrl =
    process.env.CLICKHOUSE_URL?.trim() || (clickhouseHost ? `https://${clickhouseHost}` : '')
  const clickhouseUser = process.env.CLICKHOUSE_USER?.trim() || 'default'
  const clickhousePassword = process.env.CLICKHOUSE_PASSWORD?.trim() || ''

  if (!clickhouseUrl) {
    throw new Error('Missing CLICKHOUSE_URL or CLICKHOUSE_HOST')
  }

  if (!clickhousePassword) {
    throw new Error('Missing CLICKHOUSE_PASSWORD')
  }

  return { clickhouseUrl, clickhouseUser, clickhousePassword }
}

function runCli(cwd: string, args: string[]): { exitCode: number; stdout: string; stderr: string } {
  const result = Bun.spawnSync({
    cmd: ['bun', CLI_ENTRY, ...args],
    cwd,
    stdout: 'pipe',
    stderr: 'pipe',
    env: process.env,
  })

  return {
    exitCode: result.exitCode,
    stdout: new TextDecoder().decode(result.stdout),
    stderr: new TextDecoder().decode(result.stderr),
  }
}

async function runSql(url: string, username: string, password: string, sql: string): Promise<void> {
  const auth = Buffer.from(`${username}:${password}`).toString('base64')
  const response = await fetch(url, {
    method: 'POST',
    signal: AbortSignal.timeout(10_000),
    headers: {
      Authorization: `Basic ${auth}`,
      'Content-Type': 'text/plain',
    },
    body: sql,
  })

  if (!response.ok) {
    const body = await response.text()
    throw new Error(`ClickHouse SQL failed (${response.status}): ${body}`)
  }
}

function isUnknownTableError(error: unknown): boolean {
  if (!(error instanceof Error)) return false
  return error.message.includes('UNKNOWN_TABLE')
}

async function sleep(ms: number): Promise<void> {
  await new Promise<void>((resolve) => setTimeout(resolve, ms))
}

async function runSqlWithUnknownTableRetry(
  url: string,
  username: string,
  password: string,
  sql: string,
  attempts = 5
): Promise<void> {
  for (let attempt = 1; attempt <= attempts; attempt += 1) {
    try {
      await runSql(url, username, password, sql)
      return
    } catch (error) {
      if (!isUnknownTableError(error) || attempt === attempts) throw error
      await sleep(attempt * 200)
    }
  }
}

async function runCliWithRetry(
  cwd: string,
  args: string[],
  { maxAttempts = 5, delayMs = 2000 }: { maxAttempts?: number; delayMs?: number } = {},
): Promise<{ exitCode: number; stdout: string; stderr: string }> {
  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
    const result = runCli(cwd, args)
    if (result.exitCode === 0) return result
    if (attempt === maxAttempts) return result
    await sleep(delayMs)
  }
  return runCli(cwd, args)
}

async function dropDatabase(url: string, username: string, password: string, database: string): Promise<void> {
  await runSql(url, username, password, `DROP DATABASE IF EXISTS ${database}`)
}

function renderBaseSchema(database: string): string {
  return `import { schema, table } from '${CORE_ENTRY}'\n\nconst users = table({\n  database: '${database}',\n  name: 'users',\n  columns: [\n    { name: 'id', type: 'UInt64' },\n    { name: 'email', type: 'String' },\n  ],\n  engine: 'MergeTree()',\n  primaryKey: ['id'],\n  orderBy: ['id'],\n})\n\nexport default schema(users)\n`
}

function renderUniqueProjectionSchema(database: string): string {
  return `import { schema, table } from '${CORE_ENTRY}'\n\nconst users = table({\n  database: '${database}',\n  name: 'users',\n  columns: [\n    { name: 'id', type: 'UInt64' },\n    { name: 'email', type: 'String' },\n  ],\n  engine: 'MergeTree()',\n  primaryKey: ['id'],\n  orderBy: ['id'],\n  uniqueKey: ['id'],\n  projections: [{ name: 'p_recent', query: 'SELECT id ORDER BY id DESC LIMIT 10' }],\n})\n\nexport default schema(users)\n`
}

interface E2EFixture {
  dir: string
  configPath: string
  schemaPath: string
}

async function createFixture(database: string): Promise<E2EFixture> {
  const dir = await mkdtemp(join(tmpdir(), 'chkit-cli-drift-e2e-'))
  const schemaPath = join(dir, 'schema.ts')
  const configPath = join(dir, 'clickhouse.config.ts')
  const outDir = join(dir, 'chkit')
  const migrationsDir = join(outDir, 'migrations')
  const metaDir = join(outDir, 'meta')

  const { clickhouseUrl, clickhouseUser, clickhousePassword } = getRequiredEnv()

  await writeFile(schemaPath, renderBaseSchema(database), 'utf8')
  await writeFile(
    configPath,
    `export default {\n  schema: '${schemaPath}',\n  outDir: '${outDir}',\n  migrationsDir: '${migrationsDir}',\n  metaDir: '${metaDir}',\n  clickhouse: {\n    url: '${clickhouseUrl}',\n    username: '${clickhouseUser}',\n    password: '${clickhousePassword}',\n    database: 'default',\n  },\n}\n`,
    'utf8'
  )

  return { dir, configPath, schemaPath }
}

describe('@chkit/cli drift depth env e2e', () => {
  const liveEnv = getRequiredEnv()

  test(
    'detects manual drift and exposes reason counts via drift/check JSON',
    async () => {
      const dbSuffix = `${Date.now()}_${Math.floor(Math.random() * 100000)}`
      const database = `chkit_e2e_drift_${dbSuffix}`
      const fixture = await createFixture(database)
      const { clickhouseUrl, clickhouseUser, clickhousePassword } = liveEnv

      try {
        const generated = runCli(fixture.dir, ['generate', '--config', fixture.configPath, '--json'])
        expect(generated.exitCode).toBe(0)

        const executed = await runCliWithRetry(fixture.dir, ['migrate', '--config', fixture.configPath, '--execute', '--json'])
        if (executed.exitCode !== 0) {
          throw new Error(
            `migrate --execute failed (exit=${executed.exitCode})\nstdout:\n${executed.stdout}\nstderr:\n${executed.stderr}`
          )
        }

        await runSqlWithUnknownTableRetry(
          clickhouseUrl,
          clickhouseUser,
          clickhousePassword,
          `ALTER TABLE ${database}.users ADD COLUMN rogue String`
        )
        await runSql(
          clickhouseUrl,
          clickhouseUser,
          clickhousePassword,
          `CREATE VIEW ${database}.manual_view AS SELECT 1 AS one`
        )

        const driftResult = runCli(fixture.dir, ['drift', '--config', fixture.configPath, '--json'])
        expect(driftResult.exitCode).toBe(0)
        const driftPayload = JSON.parse(driftResult.stdout) as {
          drifted: boolean
          objectDrift: Array<{ code: string }>
          tableDrift: Array<{ table: string; reasonCodes: string[] }>
        }

        expect(driftPayload.drifted).toBe(true)
        expect(driftPayload.objectDrift.some((item) => item.code === 'extra_object')).toBe(true)
        const usersTableDrift = driftPayload.tableDrift.find((item) => item.table === `${database}.users`)
        expect(usersTableDrift).toBeTruthy()
        expect(usersTableDrift?.reasonCodes).toContain('extra_column')

        const checkResult = runCli(fixture.dir, ['check', '--config', fixture.configPath, '--json'])
        expect(checkResult.exitCode).toBe(1)
        const checkPayload = JSON.parse(checkResult.stdout) as {
          failedChecks: string[]
          driftReasonCounts: Record<string, number>
          driftReasonTotals: { total: number; object: number; table: number }
        }

        expect(checkPayload.failedChecks).toContain('schema_drift')
        expect((checkPayload.driftReasonCounts.extra_object ?? 0) > 0).toBe(true)
        expect((checkPayload.driftReasonCounts.extra_column ?? 0) > 0).toBe(true)
        expect(checkPayload.driftReasonTotals.total > 0).toBe(true)
        expect(checkPayload.driftReasonTotals.object > 0).toBe(true)
        expect(checkPayload.driftReasonTotals.table > 0).toBe(true)
      } finally {
        try {
          await dropDatabase(clickhouseUrl, clickhouseUser, clickhousePassword, database)
        } catch {
          // best effort cleanup for shared env
        }
        await rm(fixture.dir, { recursive: true, force: true })
      }
    },
    240_000
  )

  test(
    'detects unique-key and projection drift reasons and exposes them in check summary',
    async () => {
      const dbSuffix = `${Date.now()}_${Math.floor(Math.random() * 100000)}`
      const database = `chkit_e2e_drift_keys_${dbSuffix}`
      const fixture = await createFixture(database)
      const { clickhouseUrl, clickhouseUser, clickhousePassword } = liveEnv

      try {
        const generated = runCli(fixture.dir, ['generate', '--config', fixture.configPath, '--json'])
        expect(generated.exitCode).toBe(0)

        const executed = await runCliWithRetry(fixture.dir, ['migrate', '--config', fixture.configPath, '--execute', '--json'])
        if (executed.exitCode !== 0) {
          throw new Error(
            `migrate --execute failed (exit=${executed.exitCode})\nstdout:\n${executed.stdout}\nstderr:\n${executed.stderr}`
          )
        }

        await writeFile(fixture.schemaPath, renderUniqueProjectionSchema(database), 'utf8')
        const driftGenerate = runCli(fixture.dir, ['generate', '--config', fixture.configPath, '--json'])
        expect(driftGenerate.exitCode).toBe(0)

        const driftResult = runCli(fixture.dir, ['drift', '--config', fixture.configPath, '--json'])
        expect(driftResult.exitCode).toBe(0)
        const driftPayload = JSON.parse(driftResult.stdout) as {
          drifted: boolean
          tableDrift: Array<{ table: string; reasonCodes: string[] }>
        }

        expect(driftPayload.drifted).toBe(true)
        const usersTableDrift = driftPayload.tableDrift.find((item) => item.table === `${database}.users`)
        expect(usersTableDrift).toBeTruthy()
        expect(usersTableDrift?.reasonCodes).toContain('unique_key_mismatch')
        expect(usersTableDrift?.reasonCodes).toContain('projection_mismatch')

        const checkResult = runCli(fixture.dir, ['check', '--config', fixture.configPath, '--json'])
        expect(checkResult.exitCode).toBe(1)
        const checkPayload = JSON.parse(checkResult.stdout) as {
          driftReasonCounts: Record<string, number>
        }
        expect((checkPayload.driftReasonCounts.unique_key_mismatch ?? 0) > 0).toBe(true)
        expect((checkPayload.driftReasonCounts.projection_mismatch ?? 0) > 0).toBe(true)
      } finally {
        try {
          await dropDatabase(clickhouseUrl, clickhouseUser, clickhousePassword, database)
        } catch {
          // best effort cleanup for shared env
        }
        await rm(fixture.dir, { recursive: true, force: true })
      }
    },
    240_000
  )

  test(
    'respects failOnDrift=false policy when drift exists',
    async () => {
      const dbSuffix = `${Date.now()}_${Math.floor(Math.random() * 100000)}`
      const database = `chkit_e2e_drift_policy_${dbSuffix}`
      const fixture = await createFixture(database)
      const { clickhouseUrl, clickhouseUser, clickhousePassword } = liveEnv

      try {
        const generated = runCli(fixture.dir, ['generate', '--config', fixture.configPath, '--json'])
        expect(generated.exitCode).toBe(0)

        const executed = await runCliWithRetry(fixture.dir, ['migrate', '--config', fixture.configPath, '--execute', '--json'])
        if (executed.exitCode !== 0) {
          throw new Error(
            `migrate --execute failed (exit=${executed.exitCode})\nstdout:\n${executed.stdout}\nstderr:\n${executed.stderr}`
          )
        }

        await runSqlWithUnknownTableRetry(
          clickhouseUrl,
          clickhouseUser,
          clickhousePassword,
          `ALTER TABLE ${database}.users ADD COLUMN rogue String`
        )

        await writeFile(
          fixture.configPath,
          `export default {\n  schema: '${fixture.schemaPath}',\n  outDir: '${join(fixture.dir, 'chkit')}',\n  migrationsDir: '${join(fixture.dir, 'chkit/migrations')}',\n  metaDir: '${join(fixture.dir, 'chkit/meta')}',\n  clickhouse: {\n    url: '${clickhouseUrl}',\n    username: '${clickhouseUser}',\n    password: '${clickhousePassword}',\n    database: 'default',\n  },\n  check: {\n    failOnDrift: false,\n  },\n}\n`,
          'utf8'
        )

        const checkResult = runCli(fixture.dir, ['check', '--config', fixture.configPath, '--json'])
        expect(checkResult.exitCode).toBe(0)
        const checkPayload = JSON.parse(checkResult.stdout) as {
          ok: boolean
          drifted: boolean
          failedChecks: string[]
          policy: { failOnDrift: boolean }
        }
        expect(checkPayload.ok).toBe(true)
        expect(checkPayload.drifted).toBe(true)
        expect(checkPayload.policy.failOnDrift).toBe(false)
        expect(checkPayload.failedChecks).not.toContain('schema_drift')
      } finally {
        try {
          await dropDatabase(clickhouseUrl, clickhouseUser, clickhousePassword, database)
        } catch {
          // best effort cleanup for shared env
        }
        await rm(fixture.dir, { recursive: true, force: true })
      }
    },
    240_000
  )

  test(
    'check --strict overrides failOnDrift=false when drift exists',
    async () => {
      const dbSuffix = `${Date.now()}_${Math.floor(Math.random() * 100000)}`
      const database = `chkit_e2e_drift_strict_${dbSuffix}`
      const fixture = await createFixture(database)
      const { clickhouseUrl, clickhouseUser, clickhousePassword } = liveEnv

      try {
        const generated = runCli(fixture.dir, ['generate', '--config', fixture.configPath, '--json'])
        expect(generated.exitCode).toBe(0)

        const executed = await runCliWithRetry(fixture.dir, ['migrate', '--config', fixture.configPath, '--execute', '--json'])
        if (executed.exitCode !== 0) {
          throw new Error(
            `migrate --execute failed (exit=${executed.exitCode})\nstdout:\n${executed.stdout}\nstderr:\n${executed.stderr}`
          )
        }

        await runSqlWithUnknownTableRetry(
          clickhouseUrl,
          clickhouseUser,
          clickhousePassword,
          `ALTER TABLE ${database}.users ADD COLUMN rogue String`
        )

        await writeFile(
          fixture.configPath,
          `export default {\n  schema: '${fixture.schemaPath}',\n  outDir: '${join(fixture.dir, 'chkit')}',\n  migrationsDir: '${join(fixture.dir, 'chkit/migrations')}',\n  metaDir: '${join(fixture.dir, 'chkit/meta')}',\n  clickhouse: {\n    url: '${clickhouseUrl}',\n    username: '${clickhouseUser}',\n    password: '${clickhousePassword}',\n    database: 'default',\n  },\n  check: {\n    failOnDrift: false,\n  },\n}\n`,
          'utf8'
        )

        const checkResult = runCli(fixture.dir, ['check', '--config', fixture.configPath, '--strict', '--json'])
        expect(checkResult.exitCode).toBe(1)
        const checkPayload = JSON.parse(checkResult.stdout) as {
          strict: boolean
          ok: boolean
          drifted: boolean
          failedChecks: string[]
          policy: { failOnDrift: boolean }
        }
        expect(checkPayload.strict).toBe(true)
        expect(checkPayload.ok).toBe(false)
        expect(checkPayload.drifted).toBe(true)
        expect(checkPayload.policy.failOnDrift).toBe(true)
        expect(checkPayload.failedChecks).toContain('schema_drift')
      } finally {
        try {
          await dropDatabase(clickhouseUrl, clickhouseUser, clickhousePassword, database)
        } catch {
          // best effort cleanup for shared env
        }
        await rm(fixture.dir, { recursive: true, force: true })
      }
    },
    240_000
  )
})

import { createClickHouseExecutor, type ClickHouseExecutor } from '@chkit/clickhouse'
import type { ChxConfig } from '@chkit/core'

import type { MigrationJournal, MigrationJournalEntry } from './migration-store.js'
import { CLI_VERSION } from './version.js'

export interface JournalStore {
  readJournal(): Promise<MigrationJournal>
  appendEntry(entry: MigrationJournalEntry): Promise<void>
}

const CREATE_TABLE_SQL = `CREATE TABLE IF NOT EXISTS _chkit_migrations (
    name String,
    applied_at DateTime64(3, 'UTC'),
    checksum String,
    chkit_version String
) ENGINE = MergeTree()
ORDER BY (name)
SETTINGS index_granularity = 1`

interface MigrationRow extends Record<string, unknown> {
  name: string
  applied_at: string
  checksum: string
  chkit_version: string
}

export function createJournalStore(db: ClickHouseExecutor): JournalStore {
  let bootstrapped = false

  async function ensureTable(): Promise<void> {
    if (bootstrapped) return
    await db.execute(CREATE_TABLE_SQL)
    bootstrapped = true
  }

  return {
    async readJournal(): Promise<MigrationJournal> {
      await ensureTable()
      const rows = await db.query<MigrationRow>(
        `SELECT name, applied_at, checksum, chkit_version FROM _chkit_migrations ORDER BY name`
      )
      return {
        version: 1,
        applied: rows.map((row) => ({
          name: row.name,
          appliedAt: row.applied_at,
          checksum: row.checksum,
        })),
      }
    },

    async appendEntry(entry: MigrationJournalEntry): Promise<void> {
      await ensureTable()
      await db.insert<MigrationRow>({
        table: '_chkit_migrations',
        values: [
          {
            name: entry.name,
            applied_at: entry.appliedAt,
            checksum: entry.checksum,
            chkit_version: CLI_VERSION,
          },
        ],
      })
    },
  }
}

export function createJournalStoreFromConfig(
  clickhouseConfig: NonNullable<ChxConfig['clickhouse']>
): { journalStore: JournalStore; db: ClickHouseExecutor } {
  const db = createClickHouseExecutor(clickhouseConfig)
  return { journalStore: createJournalStore(db), db }
}

