  await runSql(url, username, password, `DROP DATABASE IF EXISTS ${database}`)
}

function renderBaseSchema(database: string): string {
  return `import { schema, table } from '${CORE_ENTRY}'

const users = table({
  database: '${database}',
  name: 'users',
  columns: [
    { name: 'id', type: 'UInt64' },
    { name: 'email', type: 'String' },
  ],
  engine: 'MergeTree()',
  primaryKey: ['id'],
  orderBy: ['id'],
})

export default schema(users)
`
}

function renderUniqueProjectionSchema(database: string): string {
  return `import { schema, table } from '${CORE_ENTRY}'

const users = table({
  database: '${database}',
  name: 'users',
  columns: [
    { name: 'id', type: 'UInt64' },
    { name: 'email', type: 'String' },
  ],
  engine: 'MergeTree()',
  primaryKey: ['id'],
  orderBy: ['id'],
  uniqueKey: ['id'],
  projections: [{ name: 'p_recent', query: 'SELECT id ORDER BY id DESC LIMIT 10' }],
})

export default schema(users)
`
}

