17→export class ChxValidationError extends Error {
18→  readonly issues: ValidationIssue[]
19→
20→  constructor(issues: ValidationIssue[]) {
21→    super(
22→      `Schema validation failed with ${issues.length} issue${issues.length === 1 ? '' : 's'}`
23→    )
24→    this.name = 'ChxValidationError'
25→    this.issues = issues
26→  }
27→}
