29→export function defineConfig<T extends ChxUserConfig>(config: T): T
30→export function defineConfig<T extends ChxUserConfig>(config: ChxConfigFn<T>): ChxConfigFn<T>
31→export function defineConfig<T extends ChxUserConfig>(config: ChxConfigInput<T>): ChxConfigInput<T> {
32→  return config
33→}
34→
35→export function resolveConfig(config: ChxUserConfig): ChxResolvedConfig {
36→  const outDir = config.outDir ?? './chkit'
37→  const migrationsDir = config.migrationsDir ?? join(outDir, 'migrations')
38→  const metaDir = config.metaDir ?? join(outDir, 'meta')
39→
40→  return {
41→    schema: Array.isArray(config.schema) ? config.schema : [config.schema],
42→    outDir,
43→    migrationsDir,
44→    metaDir,
45→    plugins: config.plugins ?? [],
46→    check: {
47→      failOnPending: config.check?.failOnPending ?? true,
48→      failOnChecksumMismatch: config.check?.failOnChecksumMismatch ?? true,
49→      failOnDrift: config.check?.failOnDrift ?? true,
50→    },
51→    safety: {
52→      allowDestructive: config.safety?.allowDestructive ?? false,
53→    },
54→    clickhouse: config.clickhouse
55→      ? {
56→          url: config.clickhouse.url,
57→          username: config.clickhouse.username ?? 'default',
58→          password: config.clickhouse.password ?? '',
59→          database: config.clickhouse.database ?? 'default',
60→          secure: config.clickhouse.secure ?? false,
61→        }
62→      : undefined,
63→  }
64→}
