66→export function table(input: Omit<TableDefinition, 'kind'>): TableDefinition {
67→  return { ...input, kind: 'table' }
68→}
69→
70→export function view(input: Omit<ViewDefinition, 'kind'>): ViewDefinition {
71→  return { ...input, kind: 'view' }
72→}
73→
74→export function materializedView(
75→  input: Omit<MaterializedViewDefinition, 'kind'>
76→): MaterializedViewDefinition {
77→  return { ...input, kind: 'materialized_view' }
78→}
79→
80→export function schema(...definitions: SchemaDefinition[]): SchemaDefinition[] {
81→  return definitions
82→}
83→
84→export function isSchemaDefinition(value: unknown): value is SchemaDefinition {
85→  if (!value || typeof value !== 'object') return false
86→  const kind = (value as { kind?: string }).kind
87→  return kind === 'table' || kind === 'view' || kind === 'materialized_view'
88→}
