@import "tailwindcss";

html {
  overscroll-behavior: none;
}

@font-face {
  font-family: "Geist";
  src: url("/fonts/Geist-Variable.woff2") format("woff2");
  font-weight: 100 900;
  font-display: swap;
}

@theme {
  --font-geist: "Geist", sans-serif;
  --color-ob-bg: #0D0D0D;
  --color-ob-light: #E8E7E3;
  --color-ob-dark: #272727;
  --color-ob-text-dark: #170A16;
  --color-ob-subtitle: #808080;
}

/* ── Panel noise texture overlay ──────────────────── */

.panel-surface {
  position: relative;
  isolation: isolate;
}

.panel-surface::after {
  content: '';
  position: absolute;
  inset: 0;
  border-radius: inherit;
  background: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='n'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.65' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23n)'/%3E%3C/svg%3E");
  opacity: 0.03;
  mix-blend-mode: overlay;
  pointer-events: none;
  z-index: 50;
}


/* ── Animations ──────────────────────────────────── */

@keyframes breathe {
  0%, 100% { opacity: 0.5; }
  50% { opacity: 0.7; }
}

@keyframes led-glow {
  0%, 100% { box-shadow: 0 0 4px rgba(254,0,122,0.5), 0 0 8px rgba(254,0,122,0.15); }
  50% { box-shadow: 0 0 6px rgba(254,0,122,0.7), 0 0 12px rgba(254,0,122,0.25); }
}

.node-dot {
  animation: breathe 3s ease-in-out infinite;
}

.status-led {
  animation: led-glow 2s ease-in-out infinite;
}

/* ── CTA button states ───────────────────────────── */

.cta-btn {
  transition: all 0.15s ease;
}

.cta-btn:hover {
  transform: translateY(-1px);
  filter: brightness(1.05);
  box-shadow:
    inset 0 1px 0 rgba(255,255,255,0.3),
    inset 0 -2px 0 rgba(0,0,0,0.15),
    0 6px 20px rgba(254,0,122,0.35),
    0 2px 6px rgba(0,0,0,0.4) !important;
}

.cta-btn:active {
  transform: translateY(1px);
  filter: brightness(0.97);
  box-shadow:
    inset 0 2px 4px rgba(0,0,0,0.2),
    0 1px 4px rgba(254,0,122,0.15),
    0 1px 2px rgba(0,0,0,0.3) !important;
}

/* ── Compute node card interaction ───────────────── */

.node-card {
  transition: border-color 0.2s ease, box-shadow 0.2s ease;
}

.node-card:hover {
  border-color: rgba(255,255,255,0.09) !important;
}

/* ── Terminal cursor ─────────────────────────────── */

@keyframes terminal-blink {
  0%, 100% { opacity: 1; }
  50% { opacity: 0; }
}

/* ── Reduced motion ──────────────────────────────── */

@media (prefers-reduced-motion: reduce) {
  *, *::before, *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

