 23→/* ── Panel noise texture overlay ──────────────────── */
 24→
 25→.panel-surface {
 26→  position: relative;
 27→  isolation: isolate;
 28→}
 29→
 30→.panel-surface::after {
 31→  content: '';
 32→  position: absolute;
 33→  inset: 0;
 34→  border-radius: inherit;
 35→  background: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='n'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.65' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23n)'/%3E%3C/svg%3E");
 36→  opacity: 0.03;
 37→  mix-blend-mode: overlay;
 38→  pointer-events: none;
 39→  z-index: 50;
 40→}
 41→
 42→
 43→/* ── Animations ──────────────────────────────────── */
 44→
 45→@keyframes breathe {
 46→  0%, 100% { opacity: 0.5; }
 47→  50% { opacity: 0.7; }
 48→}
 49→
