 51→  0%, 100% { box-shadow: 0 0 4px rgba(254,0,122,0.5), 0 0 8px rgba(254,0,122,0.15); }
 52→  50% { box-shadow: 0 0 6px rgba(254,0,122,0.7), 0 0 12px rgba(254,0,122,0.25); }
 53→}
