	--sl-color-text: var(--sl-color-gray-2);
	--sl-color-text-accent: var(--sl-color-accent-high);
	--sl-color-text-invert: var(--sl-color-black);
	--sl-color-bg: var(--sl-color-black);
	--sl-color-bg-nav: #141414;
	--sl-color-bg-sidebar: #141414;
	--sl-color-bg-inline-code: #1E1E1E;
	--sl-color-bg-accent: var(--sl-color-accent);
	--sl-color-hairline-light: rgba(255, 255, 255, 0.08);
	--sl-color-hairline: rgba(255, 255, 255, 0.06);
	--sl-color-hairline-shade: rgba(0, 0, 0, 0.4);

	--sl-shadow-sm: 0px 1px 2px rgba(0, 0, 0, 0.3);
	--sl-shadow-md: 0px 4px 12px rgba(0, 0, 0, 0.4);
	--sl-shadow-lg: 0px 8px 24px rgba(0, 0, 0, 0.5);
}

/* Hide theme toggle — dark-only site */
starlight-theme-select {
	display: none;
}

/* ── Typography refinements ───────────────────────── */

:root {
	--sl-line-height: 1.65;
	--sl-line-height-headings: 1.1;
}

/* Tighter heading tracking (matches ob-db's tracking-[-0.03em]) */
h1, h2, h3, h4, h5, h6 {
	letter-spacing: -0.03em;
	font-weight: 600;
}

/* ── Navigation & sidebar polish ──────────────────── */

.sidebar-content a[aria-current='page'] {
	font-weight: 600;
}

/* Subtle border on nav matching ob-db's border-white/[0.08] */
nav.header {
	border-bottom: 1px solid rgba(255, 255, 255, 0.06);
}

/* ── Card styling to match ob-db panels ───────────── */

.card {
	background: #141414;
	border: 1px solid rgba(255, 255, 255, 0.06);
	border-radius: 12px;
}

.card:hover {
	border-color: rgba(255, 255, 255, 0.1);
}

/* Monochrome card icons — override Starlight's color cycling */
.card .icon {
	border-color: rgba(255, 255, 255, 0.1) !important;
	background-color: #1E1E1E !important;
	color: #808080;
}

/* ── Hero buttons — match ob-db button style ─────── */

.sl-link-button.primary {
	background: #E8E7E3;
	border-color: #E8E7E3;
	color: #170A16;
	box-shadow: inset 0px -2px 4px rgba(0, 0, 0, 0.08), inset 0px 1px 1.1px rgba(255, 255, 255, 0.35);
}

.sl-link-button.primary:hover {
	background: #F0EFEB;
	transform: translateY(-1px);
	filter: brightness(1.05);
}

.sl-link-button.secondary {
	border-color: rgba(255, 255, 255, 0.12);
	color: #E8E7E3;
	background: #272727;
	box-shadow: inset 0px -2px 4px rgba(0, 0, 0, 0.29), inset 0px 1px 1.1px rgba(255, 255, 255, 0.19);
}

.sl-link-button.secondary:hover {
	border-color: rgba(255, 255, 255, 0.18);
	transform: translateY(-1px);
	filter: brightness(1.1);
}

/* ── Code blocks — match ob-db terminal aesthetic ─── */

pre {
	background: #141414 !important;
	border: 1px solid rgba(255, 255, 255, 0.06);
	border-radius: 10px;
}

/* ── Link styling ─────────────────────────────────── */

a:not([class]) {
	text-decoration-color: rgba(254, 0, 122, 0.4);
}

a:not([class]):hover {
	text-decoration-color: #FE007A;
}

/* ── Hero section styling ─────────────────────────── */

.hero {
	border-bottom: 1px solid rgba(255, 255, 255, 0.06);
}

/* ── Smooth scrolling (matches ob-db behavior) ────── */

html {
	scroll-behavior: smooth;
}

/* ── Reduced motion ───────────────────────────────── */

@media (prefers-reduced-motion: reduce) {
	html {
		scroll-behavior: auto;
	}
}

