 3→import type { StarlightIcon } from '../components/Icons';
 4→
 5→interface Props {
 6→	icon?: StarlightIcon;
 7→	title: string;
 8→}
 9→
10→const { icon, title } = Astro.props;
11→---
12→
13→<article class="card sl-flex">
14→	<p class="title sl-flex">
15→		{icon && <Icon name={icon} class="icon" size="1.333em" />}
16→		<span set:html={title} />
17→	</p>
18→	<div class="body"><slot /></div>
19→</article>
20→
21→<style>
22→	@layer starlight.components {
23→		.card {
24→			--sl-card-border: var(--sl-color-purple);
25→			--sl-card-bg: var(--sl-color-purple-low);
26→			border: 1px solid var(--sl-color-gray-5);
27→			background-color: var(--sl-color-black);
28→			padding: clamp(1rem, calc(0.125rem + 3vw), 2.5rem);
29→			flex-direction: column;
30→			gap: clamp(0.5rem, calc(0.125rem + 1vw), 1rem);
31→		}
32→		.card:nth-child(4n + 1) {
33→			--sl-card-border: var(--sl-color-orange);
34→			--sl-card-bg: var(--sl-color-orange-low);
