  1→---
  2→import { Image } from 'astro:assets';
  3→import { PAGE_TITLE_ID } from '../constants';
  4→import LinkButton from '../user-components/LinkButton.astro';
  5→
  6→const { data } = Astro.locals.starlightRoute.entry;
  7→const { title = data.title, tagline, image, actions = [] } = data.hero || {};
  8→
  9→const imageAttrs = {
 10→	loading: 'eager' as const,
 11→	decoding: 'async' as const,
 12→	width: 400,
 13→	height: 400,
 14→	alt: image?.alt || '',
 15→};
 16→
 17→let darkImage: ImageMetadata | undefined;
 18→let lightImage: ImageMetadata | undefined;
 19→let rawHtml: string | undefined;
 20→if (image) {
 21→	if ('file' in image) {
 22→		darkImage = image.file;
 23→	} else if ('dark' in image) {
 24→		darkImage = image.dark;
 25→		lightImage = image.light;
 26→	} else {
 27→		rawHtml = image.html;
 28→	}
 29→}
 30→---
 31→
 32→<div class="hero">
 33→	{
 34→		darkImage && (
 35→			<Image
 36→				src={darkImage}
 37→				{...imageAttrs}
 38→				class:list={{ 'light:sl-hidden': Boolean(lightImage) }}
 39→			/>
 40→		)
 41→	}
 42→	{lightImage && <Image src={lightImage} {...imageAttrs} class="dark:sl-hidden" />}
 43→	{rawHtml && <div class="hero-html sl-flex" set:html={rawHtml} />}
 44→	<div class="sl-flex stack">
 45→		<div class="sl-flex copy">
 46→			<h1 id={PAGE_TITLE_ID} data-page-title set:html={title} />
 47→			{tagline && <div class="tagline" set:html={tagline} />}
 48→		</div>
 49→		{
 50→			actions.length > 0 && (
 51→				<div class="sl-flex actions">
 52→					{actions.map(
 53→						({ attrs: { class: className, ...attrs } = {}, icon, link: href, text, variant }) => (
 54→							<LinkButton {href} {variant} icon={icon?.name} class:list={[className]} {...attrs}>
 55→								{text}
 56→								{icon?.html && <Fragment set:html={icon.html} />}
 57→							</LinkButton>
 58→						)
 59→					)}
 60→				</div>
 61→			)
 62→		}
 63→	</div>
 64→</div>
 65→
 66→<style>
 67→	@layer starlight.core {
 68→		.hero {
 69→			display: grid;
 70→			align-items: center;
 71→			gap: 1rem;
 72→			padding-bottom: 1rem;
 73→		}
 74→
 75→		.hero > img,
 76→		.hero > .hero-html {
 77→			object-fit: contain;
 78→			width: min(70%, 20rem);
 79→			height: auto;
 80→			margin-inline: auto;
 81→		}
 82→
 83→		.stack {
 84→			flex-direction: column;
 85→			gap: clamp(1.5rem, calc(1.5rem + 1vw), 2rem);
 86→			text-align: center;
 87→		}
 88→
 89→		.copy {
 90→			flex-direction: column;
 91→			gap: 1rem;
 92→			align-items: center;
 93→		}
 94→
 95→		.copy > * {
 96→			max-width: 50ch;
 97→		}
 98→
 99→		h1 {
100→			font-size: clamp(var(--sl-text-3xl), calc(0.25rem + 5vw), var(--sl-text-6xl));
101→			line-height: var(--sl-line-height-headings);
102→			font-weight: 600;
103→			color: var(--sl-color-white);
104→		}
105→
106→		.tagline {
107→			font-size: clamp(var(--sl-text-base), calc(0.0625rem + 2vw), var(--sl-text-xl));
108→			color: var(--sl-color-gray-2);
109→		}
110→
111→		.actions {
112→			gap: 1rem 2rem;
113→			flex-wrap: wrap;
114→			justify-content: center;
115→		}
116→
117→		@media (min-width: 50rem) {
118→			.hero {
119→				grid-template-columns: 7fr 4fr;
120→				gap: 3%;
121→				padding-block: clamp(2.5rem, calc(1rem + 10vmin), 10rem);
122→			}
123→
124→			.hero > img,
125→			.hero > .hero-html {
126→				order: 2;
127→				width: min(100%, 25rem);
128→			}
129→
130→			.stack {
131→				text-align: start;
132→			}
133→
134→			.copy {
135→				align-items: flex-start;
136→			}
137→
138→			.actions {
139→				justify-content: flex-start;
140→			}
141→		}
142→	}
143→</style>
