  1→---
  2→import { flattenSidebar } from '../utils/navigation';
  3→import type { SidebarEntry } from '../utils/routing/types';
  4→import Icon from '../user-components/Icon.astro';
  5→import Badge from '../user-components/Badge.astro';
  6→import SidebarRestorePoint from './SidebarRestorePoint.astro';
  7→
  8→interface Props {
  9→	sublist: SidebarEntry[];
 10→	nested?: boolean;
 11→}
 12→
 13→const { sublist, nested } = Astro.props;
 14→---
 15→
 16→<ul class:list={{ 'top-level': !nested }}>
 17→	{
 18→		sublist.map((entry) => (
 19→			<li>
 20→				{entry.type === 'link' ? (
 21→					<a
 22→						href={entry.href}
 23→						aria-current={entry.isCurrent ? 'page' : undefined}
 24→						class:list={[{ large: !nested }, entry.attrs.class]}
 25→						{...entry.attrs}
 26→					>
 27→						<span>{entry.label}</span>
 28→						{entry.badge && (
 29→							<Badge
 30→								variant={entry.badge.variant}
 31→								class={entry.badge.class}
 32→								text={entry.badge.text}
 33→							/>
 34→						)}
 35→					</a>
 36→				) : (
 37→					<details
 38→						open={flattenSidebar(entry.entries).some((i) => i.isCurrent) || !entry.collapsed}
 39→					>
 40→						<summary>
 41→							<span class="group-label">
 42→								<span class="large">{entry.label}</span>
 43→								{entry.badge && (
 44→									<Badge
 45→										variant={entry.badge.variant}
 46→										class={entry.badge.class}
 47→										text={entry.badge.text}
 48→									/>
 49→								)}
 50→							</span>
 51→							<Icon name="right-caret" class="caret" size="1.25rem" />
 52→						</summary>
 53→						<SidebarRestorePoint />
 54→						<Astro.self sublist={entry.entries} nested />
 55→					</details>
 56→				)}
 57→			</li>
 58→		))
 59→	}
 60→</ul>
 61→
 62→<style>
 63→	@layer starlight.core {
 64→		ul {
 65→			--sl-sidebar-item-padding-inline: 0.5rem;
 66→			list-style: none;
 67→			padding: 0;
 68→		}
 69→
 70→		li {
 71→			overflow-wrap: anywhere;
 72→		}
 73→
 74→		ul ul li {
 75→			margin-inline-start: var(--sl-sidebar-item-padding-inline);
 76→			border-inline-start: 1px solid var(--sl-color-hairline-light);
 77→			padding-inline-start: var(--sl-sidebar-item-padding-inline);
 78→		}
 79→
 80→		.large {
 81→			font-size: var(--sl-text-lg);
 82→			font-weight: 600;
 83→			color: var(--sl-color-white);
 84→		}
 85→
 86→		.top-level > li + li {
 87→			margin-top: 0.75rem;
 88→		}
 89→
 90→		summary {
 91→			display: flex;
 92→			align-items: center;
 93→			justify-content: space-between;
 94→			padding: 0.2em var(--sl-sidebar-item-padding-inline);
 95→			line-height: 1.4;
 96→			cursor: pointer;
 97→			user-select: none;
 98→		}
 99→		summary::marker,
100→		summary::-webkit-details-marker {
101→			display: none;
102→		}
103→
104→		.caret {
105→			transition: transform 0.2s ease-in-out;
106→			flex-shrink: 0;
107→		}
108→		:global([dir='rtl']) .caret {
109→			transform: rotateZ(180deg);
110→		}
111→		[open] > summary .caret {
112→			transform: rotateZ(90deg);
113→		}
114→
115→		a {
116→			display: block;
117→			border-radius: 0.25rem;
118→			text-decoration: none;
119→			color: var(--sl-color-gray-2);
120→			padding: 0.3em var(--sl-sidebar-item-padding-inline);
121→			line-height: 1.4;
122→		}
123→
124→		a:hover,
125→		a:focus {
126→			color: var(--sl-color-white);
127→		}
128→
129→		[aria-current='page'],
130→		[aria-current='page']:hover,
131→		[aria-current='page']:focus {
132→			font-weight: 600;
133→			color: var(--sl-color-text-invert);
134→			background-color: var(--sl-color-text-accent);
135→		}
136→
137→		a > *:not(:last-child),
138→		.group-label > *:not(:last-child) {
139→			margin-inline-end: 0.25em;
140→		}
141→
142→		@media (min-width: 50rem) {
143→			.top-level > li + li {
144→				margin-top: 0.5rem;
145→			}
146→			.large {
147→				font-size: var(--sl-text-base);
148→			}
149→			a {
150→				font-size: var(--sl-text-sm);
151→			}
152→		}
153→	}
154→</style>
