 1→---
 2→import { Icons, type StarlightIcon } from '../components/Icons';
 3→
 4→interface Props {
 5→	name: StarlightIcon;
 6→	label?: string;
 7→	color?: string;
 8→	size?: string;
 9→	class?: string;
10→}
11→
12→const { name, label, size = '1em', color } = Astro.props;
13→const a11yAttrs = label ? ({ 'aria-label': label } as const) : ({ 'aria-hidden': 'true' } as const);
14→
15→/**
16→ * The fragment around the element is used as a workaround to avoid a trailing whitespace in the output.
17→ * @see https://github.com/withastro/compiler/issues/1003
18→ */
19→---
20→
21→<>
22→	<svg
23→		{...a11yAttrs}
24→		class={Astro.props.class}
25→		width="16"
26→		height="16"
27→		viewBox="0 0 24 24"
28→		fill="currentColor"
29→		set:html={Icons[name]}
30→	/>
31→</>
32→
33→<style define:vars={{ 'sl-icon-color': color, 'sl-icon-size': size }}>
34→	@layer starlight.components {
35→		svg {
36→			color: var(--sl-icon-color);
37→			font-size: var(--sl-icon-size, 1em);
38→			width: 1em;
39→			height: 1em;
40→		}
41→	}
42→</style>
