{
  "name": "@chkit/docs",
  "private": true,
  "type": "module",
  "version": "0.0.1",
  "scripts": {
    "dev": "astro dev",
    "start": "astro dev",
    "build": "astro build",
    "preview": "astro preview",
    "deploy": "wrangler pages deploy dist --project-name=chkit-docs",
    "astro": "astro"
  },
  "dependencies": {
    "@astrojs/starlight": "^0.37.6",
    "astro": "^5.6.1",
    "sharp": "^0.34.2"
  }
}

{
  "name": "@chkit/cli",
  "version": "0.1.0-beta.1",
  "type": "module",
  "main": "./dist/index.js",
  "types": "./dist/index.d.ts",
  "bin": {
    "chkit": "./dist/bin/chkit.js"
  },
  "files": [
    "dist"
  ],
  "scripts": {
    "build": "tsc -p tsconfig.json",
    "dev": "bun run src/bin/chkit.ts",
    "typecheck": "tsc -p tsconfig.json --noEmit",
    "lint": "biome lint src",
    "test": "bun test src --concurrent",
    "clean": "rm -rf dist"
  },

{
  "extends": "astro/tsconfigs/strict",
  "include": [".astro/types.d.ts", "**/*"],
  "exclude": ["dist"]
}

{
  "$schema": "https://biomejs.dev/schemas/2.3.0/schema.json",
  "files": {
    "includes": ["**", "!**/dist/**", "!**/node_modules/**", "!**/.turbo/**"]
  },
  "linter": {
    "enabled": true
  },
  "formatter": {
    "enabled": true
  },
  "javascript": {
    "formatter": {
      "semicolons": "asNeeded",
      "quoteStyle": "single"
    }
  }
}

