490â†’  }
491â†’
492â†’  let depth = 0
493â†’  for (let i = 0; i < trimmed.length; i += 1) {
494â†’    const char = trimmed[i]
495â†’    if (char === '(') depth += 1
496â†’    if (char === ')') depth -= 1
497â†’    if (depth === 0 && i < trimmed.length - 1) {
498â†’      return trimmed
499â†’    }
500â†’  }
501â†’
502â†’  return trimmed.slice(1, -1).trim()
503â†’}
504â†’
505â†’interface SystemTableRow {
506â†’  database: string
507â†’  name: string
508â†’  engine: string
509â†’  create_table_query?: string
510â†’}
511â†’
512â†’async function listNonTableRows(
513â†’  db: ReturnType<typeof createClickHouseExecutor>,
514â†’  databases: string[]
515â†’): Promise<SystemTableRow[]> {
516â†’  if (databases.length === 0) return []
517â†’  const quotedDatabases = databases.map((dbName) => `'${dbName.replace(/'/g, "''")}'`).join(', ')
518â†’  return db.query<SystemTableRow>(
519â†’    `SELECT database, name, engine, create_table_query
520â†’FROM system.tables
521â†’WHERE is_temporary = 0
522â†’  AND database IN (${quotedDatabases})
523â†’  AND engine IN ('View', 'MaterializedView')`
524â†’  )
525â†’}
526â†’
527â†’function mapSystemTableRowToDefinition(
528â†’  row: SystemTableRow
529â†’): Exclude<IntrospectedObject, IntrospectedTable> | null {
530â†’  const kind = inferSchemaKindFromEngine(row.engine)
531â†’  if (kind === 'view') {
532â†’    const as = parseAsClause(row.create_table_query)
533â†’    if (!as) return null
534â†’    return { kind: 'view', database: row.database, name: row.name, as }
535â†’  }
536â†’  if (kind === 'materialized_view') {
537â†’    const as = parseAsClause(row.create_table_query)
538â†’    const to = parseToClause(row.create_table_query, row.database)
539â†’    if (!as || !to) return null
540â†’    return { kind: 'materialized_view', database: row.database, name: row.name, to, as }
541â†’  }
542â†’  return null
543â†’}
544â†’
545â†’function parseAsClause(query: string | undefined): string | null {
546â†’  if (!query) return null
547â†’  const match = /\bAS\b([\s\S]*)$/i.exec(query)
548â†’  if (!match?.[1]) return null
549â†’  const asClause = match[1].trim().replace(/;$/, '').trim()
550â†’  return asClause.length > 0 ? asClause : null
551â†’}
552â†’
553â†’function parseToClause(
554â†’  query: string | undefined,
555â†’  fallbackDatabase: string
556â†’): { database: string; name: string } | null {
557â†’  if (!query) return null
558â†’  const identifier = /(?:^|\s)TO\s+((?:`[^`]+`|"[^"]+"|[A-Za-z0-9_]+)(?:\.(?:`[^`]+`|"[^"]+"|[A-Za-z0-9_]+))?)/i.exec(
559â†’    query
560â†’  )?.[1]
561â†’  if (!identifier) return null
562â†’  const parts = identifier.split('.').map((part) => part.replace(/^[`"]|[`"]$/g, '').trim())
563â†’  if (parts.length === 1) {
564â†’    const name = parts[0] ?? ''
565â†’    if (name.length === 0) return null
566â†’    return { database: fallbackDatabase, name }
567â†’  }
568â†’  if (parts.length === 2) {
569â†’    const database = parts[0] ?? fallbackDatabase
570â†’    const name = parts[1] ?? ''
571â†’    if (database.length === 0 || name.length === 0) return null
572â†’    return { database, name }
573â†’  }
574â†’  return null
575â†’}
576â†’
577â†’export const __testUtils = {
578â†’  summarizeSkippedObjects,
579â†’  parseAsClause,
580â†’  parseToClause,
581â†’  mapSystemTableRowToDefinition,
582â†’}
583â†’
584â†’export function renderSchemaFile(definitions: SchemaDefinition[]): string {
585â†’  const canonical = canonicalizeDefinitions(definitions)
586â†’  const declarationNames = new Map<string, number>()
587â†’  const hasTable = canonical.some((definition) => definition.kind === 'table')
588â†’  const hasView = canonical.some((definition) => definition.kind === 'view')
589â†’  const hasMaterializedView = canonical.some((definition) => definition.kind === 'materialized_view')
590â†’  const imports = ['schema']
591â†’  if (hasTable) imports.push('table')
592â†’  if (hasView) imports.push('view')
593â†’  if (hasMaterializedView) imports.push('materializedView')
594â†’  const lines: string[] = [
595â†’    `import { ${imports.join(', ')} } from '@chkit/core'`,
596â†’    '',
597â†’    '// Pulled from live ClickHouse metadata via chkit plugin pull schema',
598â†’    '',
599â†’  ]
600â†’
601â†’  const references: string[] = []
602â†’
603â†’  for (const definition of canonical) {
604â†’    const variableName = resolveTableVariableName(definition.database, definition.name, declarationNames)
605â†’    references.push(variableName)
606â†’
607â†’    if (definition.kind === 'table') {
608â†’      lines.push(`const ${variableName} = table({`)
609â†’      lines.push(`  database: ${renderString(definition.database)},`)
610â†’      lines.push(`  name: ${renderString(definition.name)},`)
611â†’      lines.push(`  engine: ${renderString(definition.engine)},`)
612â†’      lines.push('  columns: [')
613â†’
614â†’      for (const column of definition.columns) {
615â†’        const parts: string[] = [
616â†’          `name: ${renderString(column.name)}`,
617â†’          `type: ${renderString(column.type)}`,
618â†’        ]
619â†’        if (column.nullable) parts.push('nullable: true')
620â†’        if (column.default !== undefined) parts.push(`default: ${renderLiteral(column.default)}`)
621â†’        if (column.comment) parts.push(`comment: ${renderString(column.comment)}`)
622â†’        lines.push(`    { ${parts.join(', ')} },`)
623â†’      }
624â†’
625â†’      lines.push('  ],')
626â†’      lines.push(`  primaryKey: ${renderStringArray(definition.primaryKey)},`)
627â†’      lines.push(`  orderBy: ${renderStringArray(definition.orderBy)},`)
628â†’      if (definition.uniqueKey && definition.uniqueKey.length > 0) {
629â†’        lines.push(`  uniqueKey: ${renderStringArray(definition.uniqueKey)},`)
630â†’      }
631â†’      if (definition.partitionBy) {
632â†’        lines.push(`  partitionBy: ${renderString(definition.partitionBy)},`)
633â†’      }
634â†’      if (definition.ttl) {
635â†’        lines.push(`  ttl: ${renderString(definition.ttl)},`)
636â†’      }
637â†’      if (definition.settings && Object.keys(definition.settings).length > 0) {
638â†’        lines.push('  settings: {')
639â†’        for (const key of Object.keys(definition.settings).sort()) {
640â†’          const value = definition.settings[key]
641â†’          if (value === undefined) continue
642â†’          lines.push(`    ${renderKey(key)}: ${renderLiteral(value)},`)
643â†’        }
644â†’        lines.push('  },')
645â†’      }
646â†’      if (definition.indexes && definition.indexes.length > 0) {
647â†’        lines.push('  indexes: [')
648â†’        for (const index of definition.indexes) {
649â†’          lines.push(
650â†’            `    { name: ${renderString(index.name)}, expression: ${renderString(index.expression)}, type: ${renderString(index.type)}, granularity: ${index.granularity} },`
651â†’          )
652â†’        }
653â†’        lines.push('  ],')
654â†’      }
655â†’      if (definition.projections && definition.projections.length > 0) {
656â†’        lines.push('  projections: [')
657â†’        for (const projection of definition.projections) {
658â†’          lines.push(
659â†’            `    { name: ${renderString(projection.name)}, query: ${renderString(projection.query)} },`
660â†’          )
661â†’        }
662â†’        lines.push('  ],')
663â†’      }
664â†’      lines.push('})')
665â†’    } else if (definition.kind === 'view') {
666â†’      lines.push(`const ${variableName} = view({`)
667â†’      lines.push(`  database: ${renderString(definition.database)},`)
668â†’      lines.push(`  name: ${renderString(definition.name)},`)
669â†’      lines.push(`  as: ${renderString(definition.as)},`)
670â†’      lines.push('})')
671â†’    } else {
672â†’      lines.push(`const ${variableName} = materializedView({`)
673â†’      lines.push(`  database: ${renderString(definition.database)},`)
674â†’      lines.push(`  name: ${renderString(definition.name)},`)
675â†’      lines.push(`  to: { database: ${renderString(definition.to.database)}, name: ${renderString(definition.to.name)} },`)
676â†’      lines.push(`  as: ${renderString(definition.as)},`)
677â†’      lines.push('})')
678â†’    }
679â†’    lines.push('')
680â†’  }
681â†’
682â†’  lines.push(`export default schema(${references.join(', ')})`)
683â†’  return `${lines.join('\n')}\n`
684â†’}
685â†’
686â†’function resolveTableVariableName(
687â†’  database: string,
688â†’  name: string,
689â†’  counts: Map<string, number>
690â†’): string {
691â†’  const base = sanitizeIdentifier(`${database}_${name}`)
692â†’  const current = counts.get(base) ?? 0
693â†’  const next = current + 1
694â†’  counts.set(base, next)
695â†’  return next === 1 ? base : `${base}_${next}`
696â†’}
697â†’
698â†’function sanitizeIdentifier(value: string): string {
699â†’  const sanitized = value.replace(/[^a-zA-Z0-9_]/g, '_').replace(/_+/g, '_').replace(/^_+|_+$/g, '')
700â†’  if (sanitized.length === 0) return 'table_ref'
701â†’  if (/^[0-9]/.test(sanitized)) return `table_${sanitized}`
702â†’  return sanitized
703â†’}
704â†’
705â†’function renderString(value: string): string {
706â†’  return JSON.stringify(value)
707â†’}
708â†’
709â†’function renderStringArray(values: string[]): string {
710â†’  return `[${values.map((value) => renderString(value)).join(', ')}]`
711â†’}
712â†’
713â†’function renderLiteral(value: string | number | boolean): string {
714â†’  if (typeof value === 'string') return renderString(value)
715â†’  return String(value)
716â†’}
717â†’
718â†’function renderKey(value: string): string {
719â†’  if (/^[a-zA-Z_$][a-zA-Z0-9_$]*$/.test(value)) return value
720â†’  return renderString(value)
721â†’}
722â†’
723â†’async function writeSchemaFile(input: {
724â†’  outFile: string
725â†’  content: string
726â†’  overwrite: boolean
727â†’}): Promise<void> {
728â†’  if (!input.overwrite) {
729â†’    const exists = await pathExists(input.outFile)
730â†’    if (exists) {
731â†’      throw new PullConfigError(
732â†’        `Output file already exists at ${input.outFile}. Re-run with --force or set plugin option overwrite=true.`
733â†’      )
734â†’    }
735â†’  }
736â†’
737â†’  await mkdir(dirname(input.outFile), { recursive: true })
738â†’  const tempPath = join(dirname(input.outFile), `.${Date.now()}-${Math.random().toString(16).slice(2)}.tmp`)
739â†’  await writeFile(tempPath, input.content, 'utf8')
740â†’  await rename(tempPath, input.outFile)
741â†’}
742â†’
743â†’async function pathExists(path: string): Promise<boolean> {
744â†’  try {
745â†’    await access(path, constants.F_OK)
746â†’    return true
747â†’  } catch {
748â†’    return false
749â†’  }
750â†’}
751â†’

