  1â†’#!/usr/bin/env bun
  2â†’import { spawnSync } from 'node:child_process'
  3â†’import {
  4â†’	mkdirSync,
  5â†’	readdirSync,
  6â†’	readFileSync,
  7â†’	statSync,
  8â†’	writeFileSync,
  9â†’} from 'node:fs'
 10â†’import { join, resolve } from 'node:path'
 11â†’import process, { stdin, stdout } from 'node:process'
 12â†’import { createInterface } from 'node:readline/promises'
 13â†’
 14â†’type ReleaseArgs = {
 15â†’	dryRun: boolean
 16â†’}
 17â†’
 18â†’type CommandResult = {
 19â†’	stdout: string
 20â†’	stderr: string
 21â†’}
 22â†’
 23â†’type ChangesetValidationResult = {
 24â†’	files: string[]
 25â†’	invalidEntries: string[]
 26â†’}
 27â†’
 28â†’type PackageJson = {
 29â†’	name?: string
 30â†’	version?: string
 31â†’	private?: boolean
 32â†’}
 33â†’
 34â†’const TMP_DIR = resolve('.tmp')
 35â†’const LOG_FILE = resolve(TMP_DIR, `release-manual-${Date.now()}.log`)
 36â†’const STATUS_FILE = resolve(TMP_DIR, `release-manual-status-${Date.now()}.json`)
 37â†’
 38â†’export async function main(): Promise<void> {
 39â†’	mkdirSync(TMP_DIR, { recursive: true })
 40â†’	logLine(`Release log: ${LOG_FILE}`)
 41â†’
 42â†’	const args = parseArgs(process.argv.slice(2))
 43â†’
 44â†’	validateChangesets()
 45â†’	ensureRequiredTools()
 46â†’	ensureOnMainBranch()
 47â†’	ensureWorkingTreeClean()
 48â†’
 49â†’	runQualityGates()
 50â†’	ensureBetaPrereleaseMode(args.dryRun)
 51â†’
 52â†’	if (args.dryRun) {
 53â†’		runCommand('bun', [
 54â†’			'run',
 55â†’			'changeset',
 56â†’			'--',
 57â†’			'status',
 58â†’			'--verbose',
 59â†’			'--output',
 60â†’			STATUS_FILE,
 61â†’		])
 62â†’		logLine('Dry-run complete. No publish performed.')
 63â†’		logLine(`Changeset status report: ${STATUS_FILE}`)
 64â†’		return
 65â†’	}
 66â†’
 67â†’	runCommand('bun', ['run', 'version-packages'])
 68â†’
 69â†’	const confirmed = await confirmPublish()
 70â†’	if (!confirmed) {
 71â†’		fail('Publish cancelled by user.')
 72â†’	}
 73â†’
 74â†’	publishWorkspacePackages()
 75â†’
 76â†’	logLine('Release publish finished.')
 77â†’	logLine('Follow-up: commit and push version/changelog changes on main.')
 78â†’}
 79â†’
 80â†’/**
 81â†’ * Publishes all non-private workspace packages using `bun publish`.
 82â†’ *
 83â†’ * Unlike `npm publish`, `bun publish` resolves `workspace:*` references
 84â†’ * at publish time without modifying package.json on disk, so the working
 85â†’ * tree stays clean with workspace protocol references intact.
 86â†’ */
 87â†’function publishWorkspacePackages(): void {
 88â†’	const packagesDir = resolve('packages')
 89â†’	const packageDirs = readdirSync(packagesDir).filter((name) => {
 90â†’		const pkgJsonPath = join(packagesDir, name, 'package.json')
 91â†’		try {
 92â†’			statSync(pkgJsonPath)
 93â†’			return true
 94â†’		} catch {
 95â†’			return false
 96â†’		}
 97â†’	})
 98â†’
 99â†’	let published = 0
100â†’
101â†’	for (const dir of packageDirs) {
102â†’		const pkgJsonPath = join(packagesDir, dir, 'package.json')
103â†’		const pkg = JSON.parse(readFileSync(pkgJsonPath, 'utf8')) as PackageJson
104â†’
105â†’		if (pkg.private) continue
106â†’		if (!pkg.name || !pkg.version) continue
107â†’
108â†’		logLine(`Publishing ${pkg.name}@${pkg.version}...`)
109â†’		runCommand('bun', ['publish', '--tag', 'beta', '--access', 'public'], {
110â†’			cwd: join(packagesDir, dir),
111â†’		})
112â†’		published++
113â†’	}
114â†’
115â†’	logLine(`Published ${published} package(s).`)
116â†’}
117â†’
118â†’function parseArgs(argv: string[]): ReleaseArgs {
119â†’	let dryRun = false
120â†’
121â†’	for (const arg of argv) {
122â†’		if (arg === '--dry-run') {
123â†’			dryRun = true
124â†’			continue
125â†’		}
126â†’
127â†’		fail(`Unknown argument: ${arg}. Supported args: --dry-run`)
128â†’	}
129â†’
130â†’	return { dryRun }
131â†’}
132â†’
133â†’function ensureRequiredTools(): void {
134â†’	runCommand('bun', ['--version'])
135â†’	runCommand('git', ['--version'])
136â†’	runCommand('bun', ['run', 'changeset', '--', '--version'])
137â†’}
138â†’
139â†’function ensureOnMainBranch(): void {
140â†’	const result = runCommand('git', ['rev-parse', '--abbrev-ref', 'HEAD'])
141â†’	const branch = result.stdout.trim()
142â†’	if (branch !== 'main') {
143â†’		fail(`Release must run on main. Current branch: ${branch}`)
144â†’	}
145â†’}
146â†’
147â†’function ensureWorkingTreeClean(): void {
148â†’	const result = runCommand('git', ['status', '--porcelain'])
149â†’	if (result.stdout.trim().length > 0) {
150â†’		fail('Working tree is not clean. Commit or stash changes before releasing.')
151â†’	}
152â†’}
153â†’
154â†’function runQualityGates(): void {
155â†’	runCommand('bun', ['run', 'verify'])
156â†’}
157â†’
158â†’function ensureBetaPrereleaseMode(dryRun: boolean): void {
159â†’	const preState = getPreState()
160â†’
161â†’	if (preState === null) {
162â†’		if (dryRun) {
163â†’			logLine(
164â†’				'Dry-run: prerelease mode not active; would run: bun run changeset -- pre enter beta',
165â†’			)
166â†’			return
167â†’		}
168â†’
169â†’		runCommand('bun', ['run', 'changeset', '--', 'pre', 'enter', 'beta'])
170â†’		return
171â†’	}
172â†’
173â†’	if (preState.mode !== 'pre') {
174â†’		fail(
175â†’			`Unsupported prerelease mode value in .changeset/pre.json: ${preState.mode}`,
176â†’		)
177â†’	}
178â†’
179â†’	if (preState.tag !== 'beta') {
180â†’		fail(
181â†’			`Prerelease mode already active for '${preState.tag}'. Expected 'beta'.`,
182â†’		)
183â†’	}
184â†’}
185â†’
186â†’function getPreState(): { mode?: string; tag?: string } | null {
187â†’	const preFile = resolve('.changeset/pre.json')
188â†’
189â†’	try {
190â†’		const raw = readFileSync(preFile, 'utf8')
191â†’		return JSON.parse(raw) as { mode?: string; tag?: string }
192â†’	} catch {
193â†’		return null
194â†’	}
195â†’}
196â†’
197â†’function validateChangesets(): void {
198â†’	const result = collectChangesetValidation()
199â†’
200â†’	if (result.files.length === 0) {
201â†’		fail('No pending changeset files found in .changeset/*.md.')
202â†’	}
203â†’
204â†’	if (result.invalidEntries.length > 0) {
205â†’		fail(
206â†’			`Only patch changesets are allowed for this phase. Found non-patch entries:\n${result.invalidEntries.join('\n')}`,
207â†’		)
208â†’	}
209â†’}
210â†’
211â†’function collectChangesetValidation(): ChangesetValidationResult {
212â†’	const files = readdirSync(resolve('.changeset'))
213â†’		.filter((name) => name.endsWith('.md') && name !== 'README.md')
214â†’		.map((name) => resolve('.changeset', name))
215â†’
216â†’	const invalidEntries: string[] = []
217â†’
218â†’	for (const file of files) {
219â†’		const markdown = readFileSync(file, 'utf8')
220â†’		const frontMatter = extractFrontMatter(markdown)
221â†’
222â†’		if (frontMatter.length === 0) {
223â†’			continue
224â†’		}
225â†’
226â†’		const lines = frontMatter.split('\n')
227â†’		for (const line of lines) {
228â†’			const entry = parseReleaseEntry(line)
229â†’			if (!entry) {
230â†’				continue
231â†’			}
232â†’
233â†’			if (entry.bumpType !== 'patch') {
234â†’				invalidEntries.push(
235â†’					`${file}: ${entry.packageName} -> ${entry.bumpType}`,
236â†’				)
237â†’			}
238â†’		}
239â†’	}
240â†’
241â†’	return { files, invalidEntries }
242â†’}
243â†’
244â†’function extractFrontMatter(markdown: string): string {
245â†’	const match = /^---\n([\s\S]*?)\n---/.exec(markdown)
246â†’	return match?.[1]?.trim() ?? ''
247â†’}
248â†’
249â†’function parseReleaseEntry(
250â†’	line: string,
251â†’): { packageName: string; bumpType: string } | null {
252â†’	const trimmed = line.trim()
253â†’	if (trimmed.length === 0) {
254â†’		return null
255â†’	}
256â†’
257â†’	const match = /^['"]?([^'"]+)['"]?\s*:\s*(patch|minor|major)\s*$/.exec(
258â†’		trimmed,
259â†’	)
260â†’	if (!match) {
261â†’		return null
262â†’	}
263â†’
264â†’	return {
265â†’		packageName: match[1],
266â†’		bumpType: match[2],
267â†’	}
268â†’}
269â†’
270â†’async function confirmPublish(): Promise<boolean> {
271â†’	const rl = createInterface({ input: stdin, output: stdout })
272â†’	try {
273â†’		const answer = await rl.question('Proceed with bun publish to beta? [y/N] ')
274â†’		const normalized = answer.trim().toLowerCase()
275â†’		return normalized === 'y' || normalized === 'yes'
276â†’	} finally {
277â†’		rl.close()
278â†’	}
279â†’}
280â†’
281â†’function runCommand(
282â†’	command: string,
283â†’	args: string[],
284â†’	options?: { cwd?: string },
285â†’): CommandResult {
286â†’	const renderedCommand = `${command} ${args.map(shellQuote).join(' ')}`
287â†’	logLine(`$ ${renderedCommand}`)
288â†’
289â†’	const result = spawnSync(command, args, {
290â†’		cwd: options?.cwd ?? process.cwd(),
291â†’		encoding: 'utf8',
292â†’		env: process.env,
293â†’	})
294â†’
295â†’	const stdoutText = result.stdout ?? ''
296â†’	const stderrText = result.stderr ?? ''
297â†’
298â†’	if (stdoutText.length > 0) {
299â†’		process.stdout.write(stdoutText)
300â†’		appendLog(stdoutText)
301â†’	}
302â†’
303â†’	if (stderrText.length > 0) {
304â†’		process.stderr.write(stderrText)
305â†’		appendLog(stderrText)
306â†’	}
307â†’
308â†’	if (result.status !== 0) {
309â†’		fail(`Command failed (${result.status}): ${renderedCommand}`)
310â†’	}
311â†’
312â†’	return {
313â†’		stdout: stdoutText,
314â†’		stderr: stderrText,
315â†’	}
316â†’}
317â†’
318â†’function shellQuote(value: string): string {
319â†’	if (/^[A-Za-z0-9_./:-]+$/.test(value)) {
320â†’		return value
321â†’	}
322â†’
323â†’	return `'${value.replaceAll("'", "'\\''")}'`
324â†’}
325â†’
326â†’function logLine(message: string): void {
327â†’	process.stdout.write(`${message}\n`)
328â†’	appendLog(`${message}\n`)
329â†’}
330â†’
331â†’function appendLog(message: string): void {
332â†’	writeFileSync(LOG_FILE, message, { encoding: 'utf8', flag: 'a' })
333â†’}
334â†’
335â†’function fail(message: string): never {
336â†’	logLine(`ERROR: ${message}`)
337â†’	process.exit(1)
338â†’}
339â†’
340â†’await main()
